create table company_feature
(
    id           bigserial
        constraint company_feature_pk
            primary key,
    company_name varchar(255)
        constraint company_feature_crypto_company_name_fk
            references crypto_company (name),
    feature      text
);

alter table company_feature
    owner to application;

create unique index company_feature_id_uindex
    on company_feature (id);

