create table company_gallery
(
    id           bigserial
        constraint company_gallery_pk
            primary key,
    company_name varchar(255)
        constraint company_gallery_crypto_company_name_fk
            references crypto_company (name),
    gallery      text
);

alter table company_gallery
    owner to application;

create unique index company_gallery_id_uindex
    on company_gallery (id);

