create table company_portfolio
(
    id           bigserial
        constraint company_portfolio_pk
            primary key,
    company_name varchar(255)
        constraint company_portfolio_crypto_company_name_fk
            references crypto_company (name),
    portfolio    text
);

alter table company_portfolio
    owner to application;

create unique index company_portfolio_id_uindex
    on company_portfolio (id);

