create table company_team
(
    id           bigserial
        constraint company_team_pk
            primary key,
    company_name varchar(255)
        constraint company_team_crypto_company_name_fk
            references crypto_company (name),
    team         text
);

alter table company_team
    owner to application;

create unique index company_team_id_uindex
    on company_team (id);

