INSERT INTO public.crypto_glossary (id, name, define) VALUES (1, 'Whitelist', 'A spam filtering mechanism where only users registered to the list will be granted communications or access.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (2, 'Whale', 'A cryptocurrency investor with enough resources to move the market price significantly with large buy or sell orders.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (3, 'Wallet Address', 'Similar to an email address, a wallet address acts as a destination for crypto to be sent to or received.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (4, 'Wallet', 'A software program that stores public and private keys, allowing the user to interact and perform transactions on blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (5, 'WAGMI', 'Abbreviation for “we’re all going to make it,” i.e.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (6, 'Vapourware', 'When a heavily advertised or promised product never materialises.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (7, 'UTXO', 'Unspent transaction output.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (8, 'TVL', 'An abbreviation for total value locked.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (9, 'Turing Complete', 'A system that can be used to solve any possible computational problem.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (10, 'TOR', 'The Onion Router.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (11, 'TestNet', 'An alternative blockchain running separate to the main chain used for developers to prototype software and updates to ensure stability before launch.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (12, 'Tangle', 'IOTA’s DAG based alternative to the blockchain data structure, aimed predominantly at the IoT sector.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (13, 'Solidity', 'A programming language based around EMCA Script and by extension JavaScript, created specifically for writing Smart contracts.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (14, 'Soft wallet', 'A software based wallet that is able to be hosted as a desktop, mobile or web application, many providing cross-platform integration with all three.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (15, 'Soft fork', 'A change to the software or protocol that allows for backwards compatible with previous versions.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (16, 'Smart Contracts', 'A digital protocol used to facilitate and enforce the negotiation of a contract without use of a third party.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (17, 'Skein', 'One of the five finalists of the NIST hash function competition, based on the Threefish tweakable block cypher.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (18, 'Sidechain', 'A separate blockchain, attached to a parent blockchain that allows assets to be interchangeable, in turn increasing the scope, throughput and availability of their parent chains.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (19, 'Sharding', 'A scalability solution for the Ethereum blockchain that validates the blockchain in a parallelised manner.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (20, 'SHA', 'Stands for Secure Hashing Algorithm: a standard of hashing algorithm developed by the National Institute of Standards and Technology, the latest iteration being labeled SHA-3.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (21, 'Seed phrase', 'Used as a point of origin for certain wallets, it uses a mnemonic phrase to generate a wallet ID which can then be used to regenerate the wallet in the case it gets erased or misplaced.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (22, 'Scrypt', 'A power efficient hashing algorithm that is more RAM intensive than SHA-256, it saw its debut in the cryptocurrency market as the algorithm of choice for the Litecoin blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (23, 'Satoshi Nakamoto', 'The pseudonymous name used by the original inventor of the Bitcoin cryptocurrency.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (24, 'Satoshi', 'The smallest quantifiable amount of Bitcoin, one-billionth of a single Bitcoin or 0.000000001 BTC.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (25, 'RSI', 'Relative Strength Index A common trading metric that provides a visual indicator of general market trends.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (26, 'Ring signature', 'A digital signature topology developed in 2001 leveraged in security focused cryptocurrencies, as a mechanism to anonymise transactions.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (27, 'Raiden Network', 'An ERC-20 compatible token designed to leverage secure payment channels in order to create an off-chain scaling solution for the Ethereum Blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (28, 'QR code', 'Quick response code.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (29, 'Pump and dump', 'The process of buying a large amount of tokens at once, usually as a co-ordinated group, in order to artificially inflate or ‘pump’ the price causing a market reaction as outsiders attempt to ride the upward trend, then selling or ‘dumping’ at the highest point.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (30, 'Proof of Work', 'A transaction validation system that requires the use of a computationally intensive process within the network that acts as a deterrent to prevent denial of Service or like-wise spam attacks.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (31, 'Proof of Stake', 'A power efficient transaction validation system in where users must ‘stake’ their coins in order to mint new blocks, minting of fraudulent transactions will cause staked coins to be lost.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (32, 'Proof of Capacity', 'Also known as proof of space.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (33, 'Proof of Authority', 'A centralised validation method that gives the right to validate to only to eligible parties that have been approved by an authority.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (34, 'Private Key', 'Operates in tandem with a Public key as part of common asymmetric cryptography standards, this key should never be shared.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (35, 'Price wall', 'An accumulation of buy or sell orders at a specific price.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (36, 'Pre-sale', 'A token event sale that operates before an ICO campaign, often with lower targets.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (37, 'Paper Wallet', 'An offline wallet that is simply the private key and address printed on paper, usually alongside a QR code, one of the safest ways of storing cryptocurrencies.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (38, 'P2P', 'Stands for Peer to Peer.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (39, 'Oracles', 'A third party data feed used by smart contracts to trigger events.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (40, 'Open source', 'Originating from the software industry, it is a program whose source code is made freely available to anybody, allowing them to study, change and distribute the software to anyone for any purpose.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (41, 'Off-chain', 'A transaction or process that occurs outside the main blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (42, 'Nonce', 'A random arbitrary number used to timestamp or otherwise ensure the order of a transaction in order to prevent against a replay attack.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (43, 'Node', 'A point of conversion in the network that acts as a redistribution or end point.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (44, 'NGMI', 'An abbreviation for not going to make it.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (45, 'NFT', 'An abbreviation for non-fungible token.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (46, 'MultiSig', 'A digital signature scheme which allows multiple user to sign a contract or wallet.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (47, 'Mt Gox', 'A Japanese cryptocurrency exchange that in 2013 accounted for over 70% of all bitcoin transactions, hacked in 2014 and filing for bankruptcy, its influence is still being felt today.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (48, 'Moving Average Convergence Divergence', 'A technical analysis tool used to forecast future prices by indicating the relationship between two moving averages to provide a metric for price momentum.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (49, 'Moores law', 'An observational rule of thumb credited to Gordon Moore in 1965 stating that the density of integrated circuits will double every approximately two years.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (50, 'Mooning', 'The process where market value of an asset skyrockets, exceeding investor’s expectations, often used as a meme.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (51, 'Mining', 'A process where algorithms use mathematical function to guess the hash of a block.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (52, 'MEW', 'My Ether Wallet.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (53, 'Metaverse', 'A virtual world where, one day, we will all interact, create, work, and live our lives.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (54, 'Merkel Tree', 'Also known as a Hash Tree, is a data structure where every child node contains a hashing label of the node that exists above it in the tree.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (55, 'MD5', 'A hashing function developed in 1992 due to known vulnerabilities predominately used for checksums and data validity.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (56, 'Market Capitalization', 'A measurement of the market valuation for outstanding tokens.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (57, 'Mainchain', 'Referring to processes or transactions that occur on the most currently released version of a particular blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (58, 'Lightning Network', 'A blockchain scalability model that greatly expands throughput, speed, cost effectiveness and compatibility.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (59, 'Lambo', 'A meme referring to the luxury vehicle investors will buy with their new found crypto wealth.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (60, 'KYC', 'Know Your Customer.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (61, 'Keccak', 'A family of cryptographic hashing functions which utilize sponge construction in order to provide wider random permutation, leading to greater flexibility in its use cases.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (62, 'IoT', 'Internet of Things.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (63, 'Initial coin offering', 'Similar to Crowdsale, provides blockchain with a one-time event allowing investors to buy tokens at a discounted rate and provide the chain with start-up capital, similar to an IPO in the stock market.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (64, 'Index fund', 'A passively managed fund, where investors are buying into a portfolio that a particular segment of the market, called an index Examples include the S&P 500 & Vanguard 500.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (65, 'HODL', 'A meme in the crypto community that is a purposeful misspelling of the word “hold.” Another interpretation for HODL is an acronym meaning, “hold on for dear life.”');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (66, 'HD Wallets', 'Hierarchical Deterministic Wallet.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (67, 'Hashing', 'The process of transforming data using a mathematical function into a value or key that is reprehensive of the original data set.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (68, 'Hard Wallet', 'A wallet built upon a secure hardware platform that implement processes and platform specific software to provide greater security for their users.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (69, 'Hard Fork', 'A software of protocol change that renders previous versions incompatible.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (70, 'Halving', 'Refers to the process where the payout for reward mechanism for blockchain miners is halved when particular conditions are met.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (71, 'Gwei', 'The denomination in which gas prices are paid for on the Ethereum blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (72, 'GPU', 'Graphics Processing Unit.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (73, 'Golden Cross', 'A golden cross occurs when a short-term moving average crosses above a long-term moving average signaling a potential bullish breakout.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (74, 'GM', 'An abbreviation for good morning, which in crypto is a friendly and quick way of wishing your followers or community a joyous day.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (75, 'Genesis Block', 'The first block of a blockchain, almost always hardcoded into the software clients — the only block in the chain that has no reference to a previous block.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (76, 'Gas Price', 'The maximum a user is willing to reward a miner in order to have their transaction processed, higher prices ensure faster transaction times.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (77, 'Gas', 'A measurement given to functions on the Ethereum blockchain to denote how computationally expensive they will be, this is offered to the miners who complete the process as a reward.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (78, 'Futures Contract', 'An agreement that can be traded on exchanges that stipulates a buy or sell price of an asset at a specific price and time.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (79, 'FUDster', 'Someone who uses an elevated media platform or social network to perpetuate Fear Uncertainty and Doubt through the investing community in order to effect prices.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (80, 'FUD', 'Fear, Uncertainty and Doubt that affects markets, usually in a negative fashion.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (81, 'FOMO', 'Fear of Missing Out.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (82, 'Flippening', 'A scenario in where Ethereum overtakes Bitcoin as the dominant cryptocurrency.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (83, 'Fiat', 'Currency that has no direct material backing, usually in reference to debt based markets such as USD.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (84, 'Faucet', 'A site or application that will reward it’s users with a cryptocurrency for completing specified tasks and assignments.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (85, 'EVM', 'Ethereum Virtual Machine.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (86, 'Ethereum', 'An open source blockchain that acts as a distributed computing platform and operating system, allowing decentralized applications and smart contracts be executed.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (87, 'Escrow', 'A service that provides transaction security by acting as an impartial trusted third party, holding onto an asset until the terms of a contract have been fulfilled.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (88, 'ERC20', 'A protocol that outlines the specific functions and events that allow blockchains to interact with other via the Ethereum Blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (89, 'ENS', 'Similar to a DNS within internet infrastructure, it allows user to obtain a “custom domain” .eth address, instead of using regular hexadecimal hash.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (90, 'ELI5', 'Explain it Like I’m 5.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (91, 'Distributed Ledger', 'A digital record of transactions that is maintained by a group of clients where changes and additions are verified across all parties to ensure no tampering has taken place.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (92, 'Digital Signature', 'A unique digital identifier that allows for a user to distinctly recognise and validate a file or transaction, usually through the use of a cryptographic hash similar to a public key.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (93, 'DEX', 'Decentralized Exchange.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (94, 'Delegated Proof of Stake', 'Takes the fundamentals of proof of stake and adds a democratic element whereby the community elect Witness’ to secure the network and vote power is determined by the size of their staked assets.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (95, 'DeFi', 'Short for decentralized finance.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (96, 'Death Cross', 'A death cross appears on a chart when an asset’s short-term moving average crosses below the long-term moving average indicating the potential for a major selloff.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (97, 'DDoS', 'Distributed Denial of Service.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (98, 'dApp', 'Stands for Decentralised Application.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (99, 'DAO', 'Decentralised Autonomous Organization.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (100, 'DAG', 'Directed Acyclical Graph.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (101, 'Crowdsale', 'The process where a blockchain offers investors newly minted tokens or coins, often at a discounted rate, in exchange for capital.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (102, 'CPU', 'Central computing unit.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (103, 'Consensus algorithm', 'Ensures that all users taking part in the minting process maintain the same version the chain, whilst preventing any single party from using their power to derail or negatively direct the chain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (104, 'Cold Storage', 'A storage method that keeps the wallet off of the internet when it is not being actively used.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (105, 'Byzantine fault tolerance', 'A consensus mechanism that offers secure validation across a distributed set of data, stemming from information propagation theory labelled as the Byzantine Generals problem.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (106, 'Burned', 'A process where Cryptocurrencies remove stored tokens from the circulating supply.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (107, 'Bullish', 'A descriptive term borrowed from stock markets to describe a positive market outlook.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (108, 'Block Time', 'The average time it takes for the network to find a solution to the block hash.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (109, 'Block Reward', 'A mechanism that awards miners with newly minted bitcoin, acting as an incentive for users to add hashing power to the network.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (110, 'Block Height', 'The number of blocks in the chain between the current block being minted and the genesis block.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (111, 'Block Explorer', 'Similar to the File Explorer of a computer’s operating system.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (112, 'Block', 'Blocks are linearly arranged data structures that permanently record the transaction data on a blockchain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (113, 'Black Swan Event', 'This term is used to refer to the most unexpected event that has the maximum possibility to occur in the market.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (114, 'Bid-Ask Spread', 'It is the difference between the highest bid price and the lowest ask price of an asset.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (115, 'Bid Price', 'The highest price a buyer is willing to pay for an asset.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (116, 'Beta', 'When used to indicate software status, it refers to an early test version of a program.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (117, 'BEP-95', 'A Binance Evolution Proposal that introduces a real-time burning mechanism to Binance Smart Chain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (118, 'BEP-721', 'The technical standard to create non-fungible tokens (NFTs) on the Binance Smart Chain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (119, 'BEP-20', 'It is a token standard to use when transferring crypto assets from other chains to the Binance Smart Chain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (120, 'BEP-2', 'Short for “Binance Chain Evolution Proposal 2.” It defines a set of technical rules and specifications to create a new token on the Binance Chain.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (121, 'BEF', 'Short for “Binance Ecosystem Fund.” It is an initiative founded by Binance to establish collaborations within the blockchain and cryptocurrency ecosystem.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (122, 'Bearish', 'A descriptive term borrowed from stock markets to describe a negative market outlook.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (123, 'BCF', 'Short for “Binance Blockchain Charity Foundation.” BCF is the world’s first decentralized charity platform.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (124, 'Bag Holder', 'Someone who has bought at a recent high and is left holding onto their asset, waiting to sell at the next point it breaks even.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (125, 'Attack Surface', 'The entry points for unauthorized access into a system.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (126, 'Atomic Swaps', 'A medium to perform direct, peer to peer cross chain exchanges from one blockchain to another, without an intermediate trusted third party or escrow service.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (127, 'ATH', 'The All Time High price of a cryptocurrency.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (128, 'Ask Price', 'The lowest price sellers determine as willing to accept.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (129, 'ASIC', 'Application Specific integrated circuits.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (130, 'Ascending triangle', 'An ascending triangle is generally considered to be a continuation pattern, meaning that the pattern is significant if it occurs within an uptrend or downtrend.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (131, 'Arbitrage', 'Taking advantage of variable prices across multiple exchanges in order to sell assets at a profit, despite little to no market movement.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (132, 'APU', 'Accelerated Processing Unit.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (133, 'API', 'Short for “Application Programming Interface.” It is a piece of code that enables interaction between software to enable various tasks like obtaining real market data or execute trades');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (134, 'Angel Investor', 'Private investors who provide financial support to new startups in return for equity rights.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (135, 'AML', 'Short for “Anti Money Laundering.” A set of regulations determined by a nation to combat money laundering.');
INSERT INTO public.crypto_glossary (id, name, define) VALUES (136, 'Altcoin', 'Short for “Alternative Coin”, refers to all coins except Bitcoin.');
