create table crypto_people
(
    id        bigserial
        constraint crypto_people_pk
            primary key,
    name      varchar(255),
    position  varchar(255),
    company   varchar(255),
    avatar    text,
    blog      varchar(255),
    medium    varchar(255),
    reddit    varchar(255),
    twitter   varchar(255),
    youtube   varchar(255),
    facebook  varchar(255),
    linkedin  varchar(255),
    telegram  varchar(255),
    instagram varchar(255),
    about     text,
    website   text
);

alter table crypto_people
    owner to application;

create unique index crypto_people_name_uindex
    on crypto_people (name);

create unique index crypto_people_id_uindex
    on crypto_people (id);

