INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (20, 'Tyler Winklevoss', 'CEO & Co-Founder', 'Gemini', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-tyler-winklevoss.jpg&w=60&h=60&q=75', null, 'https://medium.com/@tylerwinklevoss', null, 'https://twitter.com/tylerwinklevoss', null, 'https://www.facebook.com/twinklevoss', 'https://www.linkedin.com/in/tylerwinklevoss/', null, 'https://www.instagram.com/tylerwinklevoss/', 'Tyler Winklevoss is the co-founder and CEO of Gemini, a next-generation Bitcoin exchange. He graduated from Harvard University with a degree in Economics and earned an MBA from Oxford University. In 2008, he represented the United States at the 2008 Olympics Games in Beijing, China, placing 6th. Winklevoss has been an angel investor and entrepreneur in emerging technologies since 2003.', 'https://twitter.com/tylerwinklevoss');
