INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (40, 'Brian Kelly', 'Founder & CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/06/person-brian-kelly.jpg&w=60&h=60&q=75', null, 'https://medium.com/@BKBrianKelly', null, 'https://twitter.com/bkbriankelly', null, null, 'https://www.linkedin.com/in/bk-brian-kelly/', null, null, 'Brian Kelly founded BKCM in 2013 after experiencing a well-publicized conversion about the investment potential of Bitcoin and its supporting blockchain technology, an epiphany he chronicled in his book The Bitcoin Big Bang. As CEO, he brings more than two decades of experience managing global macro strategies, as well as building investment funds and independent investment firms from the ground up.', 'https://twitter.com/bkbriankelly');
