INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (44, 'Nouriel Roubini', 'Professor of Economics', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-nouriel-roubini.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/Nouriel', null, 'https://www.facebook.com/Nouriel-Roubini-49973139705/', 'https://www.linkedin.com/in/nourielroubini/', null, null, 'Nouriel Roubini is a professor of economics at New York University’s Stern School of Business. He is also CEO of Roubini Macro Associates, LLC, a global macroeconomic consultancy firm in New York, as well as Co-Founder of Rosa & Roubini Associates based out of London. At a 2006 address to the International Monetary Fund, Roubini warned of the impending recession due to the credit and housing market bubble.', 'https://nourielroubini.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (45, 'Jeff Garzik', 'Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/06/person-jeff-garzik-1.jpg&w=60&h=60&q=75', null, 'https://medium.com/@jgarzik', null, 'https://twitter.com/jgarzik', null, null, 'https://www.linkedin.com/in/jeffgarzik/', null, null, 'Jeff Garzik, better known as the CEO of blockchain startup Bloq, now believes his prior work could be revived in a way that promotes interoperability between the increasingly fragmented set of protocols bearing the bitcoin name. He was also a contributor to Bitcoin Core.', 'https://twitter.com/jgarzik');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (273, 'Elon Musk', 'CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2021/02/elon-musk-photo.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/elonmusk', null, null, null, null, null, 'Elon Musk is a South African-born American entrepreneur and businessman who founded X.com in 1999 (which later became PayPal), SpaceX in 2002 and Tesla Motors in 2003. Musk became a multimillionaire in his late 20s when he sold his start-up company, Zip2, to a division of Compaq Computers.', 'https://twitter.com/elonmusk');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (46, 'Amber Baldet', 'Co-Founder & CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/person-amber-baldet.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/amberbaldet', null, null, 'https://www.linkedin.com/in/abaldet/', null, null, 'Amber is co-founder and CEO of Clovyr, a company reshaping how businesses connect to each other and the world’s data. Having previously led JPMorgan’s blockchain efforts and appearing on 2017’s Fortune’s 40 Under 40 list of the most influential young people in business, she “handily bridges the divide between the Wall Street and crypto sets.” Amber also serves on the Board of the Zcash Foundation, a non-profit dedicated to building Internet payment and privacy infrastructure for the public good.', 'http://amberbaldet.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (47, 'Jesse Powell', 'CEO & Co-Founder', 'Kraken', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/person-jesse-powell.jpg&w=60&h=60&q=75', 'https://jesse.forthewin.com/', 'https://medium.com/@jespow', null, 'https://twitter.com/jespow', null, null, 'https://www.linkedin.com/in/jessepowell/', null, null, 'Jesse Powell is an expert in virtual currencies, online/international payments and fraud, as well as co-founder and CEO of Kraken, one of the most trusted and largest Bitcoin exchanges in the world with number one EUR market share. He is a 15-year veteran of the virtual currency industry.', 'https://twitter.com/jespow');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (48, 'Bill Barhydt', 'Founder & CEO', 'Abra', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/person-bill-barhydt.jpg&w=60&h=60&q=75', null, 'https://medium.com/@billbar', null, 'https://twitter.com/billbarhydt', null, 'https://www.facebook.com/billbar', 'https://www.linkedin.com/in/billbar/', null, 'https://www.instagram.com/billbar', 'After working for the CIA, NASA and Goldman Sachs, Bill Barhydt decided to join Netscape working on telecom and Internet banking deals, mostly in Europe. After the AOL acquisition of Netscape, Bill founded WebSentric. He will be speaking at our upcoming summit about ‘How programmable money is set to drive the next wave of financial innovation in retail banking and beyond’.', 'https://twitter.com/billbarhydt');
