INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (50, 'Andy Cheung', 'Head of Operations', 'OKX', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-andy-cheung.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/AndyC0125', null, null, 'https://www.linkedin.com/in/andy-cheung-025b5649/', null, null, 'Andy is an entrepreneur par excellence. The former CEO of Groupon forayed into many sectors throughout his career including P&L management, sales & marketing, operations and people management with astounding success. His leadership, e-commerce insights and marketing expertise has strengthened the culture of innovation and infused immutable quality in business operations.', 'https://twitter.com/AndyC0125');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (51, 'Wei Zhou', 'CFO', 'Binance', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/person-wei-zhou.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/weizhouBinance', null, null, 'https://www.linkedin.com/in/wei-zhou-378618107/', null, null, 'Wei Zhou serves as CFO at Binance. Wei brings over 15 years of executive experiences at leading technology, internet, and media companies, where he has built up particular expertise in financial management and accounting, capital markets and investments, as well as growing and scaling business operations in both China and the United States.', 'https://twitter.com/weizhouBinance');
