INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (53, 'Yoni Assia', 'CEO & Founder', 'eToro', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/person-yoni-assia.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/yoniassia', null, 'https://www.facebook.com/yoni.assia', 'https://www.linkedin.com/in/yoniassia/?originalSubdomain=il', null, null, 'Yoni Assia is the CEO of eToro, the world’s largest social investment network. He has shown an interest in finance and computer science since his youth and so decided to merge his passions. It was this very passion along with the social revolution, which led to the founding and development of eToro’s investment network, where users connect, share, trade and invest in the world’s financial markets.', 'https://twitter.com/yoniassia');
