INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (56, 'Chris Burniske', 'Partner', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/perosn-chris-burniske.jpg&w=60&h=60&q=75', null, 'https://medium.com/@cburniske', null, 'https://twitter.com/cburniske', null, null, 'https://www.linkedin.com/in/burniske/', null, null, 'Chris Burniske is a partner at Placeholder, a New York venture firm that specializes in cryptoassets. Prior to Placeholder, he pioneered ARK Investment Management’s Next Generation Internet strategy, leading the firm to become the first public fund manager to invest in bitcoin. His commentary has been featured on national media outlets, including the Wall Street Journal, the New York Times, Fortune, Forbes, and more. Most recently, he co-authored the best-selling book, Cryptoassets: The Innovative Investor’s Guide to Bitcoin and Beyond (McGraw Hill, 2017).', 'https://twitter.com/cburniske');
