INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (91, 'John McAfee', 'CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-john-mcafee.jpg&w=60&h=60&q=75', null, 'https://medium.com/@officialmcafee', null, 'https://twitter.com/officialmcafee', 'https://www.youtube.com/channel/UCxoo1v5Zp15ZrujZ8OxColQ', 'https://www.facebook.com/officialmcafee/', null, null, 'https://www.instagram.com/officialjohnmcafee/', 'John McAfee is a world-famous tech CEO, computer scientist, civil disobedience activist, privacy advocate, and pioneer of the commercial anti-virus industry, and recent Libertarian candidate for President of the United States. McAfee believes that a return to the founding principles of Liberty and Justice for all is America’s only hope to restore sanity to our government and society.', 'http://mcafee2020.com/?utm_source=cryptoslate');
