INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (96, 'Travis Kling', 'CIO & Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-travis-kling.jpg&w=60&h=60&q=75', null, 'https://medium.com/@traviskling', null, 'https://twitter.com/Travis_Kling', null, null, 'https://www.linkedin.com/in/travis-kling-ikigai/', null, null, 'Travis Kling spent ten years in traditional finance before founding Ikigai. Most recently, he was an L/S Equity Portfolio Manager ($200mm) at Point72 ($90bn) through 2017. Prior to that, Travis was an Energy Analyst at Magnetar Capital ($4.6bn), 2011 to 2015, investing across the capital structure in L/S equities and non-control private equity and debt.', 'https://twitter.com/Travis_Kling');
