INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (100, 'Joseph Muscat', 'Prime Minister', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-joseph-muscat.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/JosephMuscat_JM', 'https://www.youtube.com/channel/UC6zHwBlhG9TkRY-r-jmn_bQ', 'https://www.facebook.com/josephmuscatdotcom', null, null, 'https://www.instagram.com/josephmuscat_jm/', 'Dr. Joseph Muscat was born on the 22nd of January 1974. He graduated with a Bachelor of Commerce in Management and Public Policy; a Bachelor of Arts with Honours in Public Policy and a Master of Arts in European Studies from the University of Malta. In 2007, he attained a Doctorate of Philosophy in Management Research from the University of Bristol with a thesis on Fordism, multinationals, and SMEs in Malta. Dr. Muscat successfully contested the first European Parliamentary elections in Malta in 2004. Sitting with the Party of European Socialists, he held the post of Vice-President of the Parliament’s Committee on Economic and Monetary Affairs and substitute member of the Committee on the Internal Market and Consumer Protection.', 'https://maltafqalbna.com/?utm_source=cryptoslate');
