INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (104, 'Jay Clayton', 'Chairman', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-jay-clayton.jpg&w=60&h=60&q=75', null, null, null, null, null, null, null, null, null, 'Jay Clayton was nominated to chair the U.S. Securities and Exchange Commission on January 20, 2017, by President Donald Trump and sworn in on May 4, 2017.', 'https://cryptoslate.com/sec-chair-jay-clayton-no-bitcoin-etfs-until-there-is-greater-oversight-assume-your-ico-is-a-security/');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (105, 'Jack Dorsey', 'Founder & CEO', 'Cash App', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-jack-dorsey.jpg&w=60&h=60&q=75', null, 'https://medium.com/@jack', null, 'https://twitter.com/jack', null, 'https://www.facebook.com/pages/Jack-Dorsey/144427195567802?fref=ts', 'https://www.linkedin.com/in/jack-dorsey-31506b155/', null, 'http://www.instagram.com/jack', 'Jack Dorsey is an American computer programmer and internet entrepreneur widely known as a co-founder and CEO of Twitter, and as the founder and CEO of Square, a mobile payments company. In 2008, he was named to the MIT Technology Review TR35 as one of the top 35 innovators in the world under the age of 35. For 2012, The Wall Street Journal gave him the “Innovator of the Year Award” for technology.', 'https://twitter.com/jack');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (106, 'Zooko Wilcox', 'Founder & CEO', 'Zcash', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-zooko-wilcox.jpg&w=60&h=60&q=75', null, 'https://medium.com/@zooko', null, 'https://twitter.com/zooko', null, null, 'https://www.linkedin.com/in/zookow/', null, null, 'Zooko is one of the original cypherpunks. He is a long-serving technologist and entrepreneur. His experience spans open, decentralized systems; cryptography; information security; and startups. In more than 25 years in the industry, Zooko has contributed to an array of projects — many of which champion privacy implementations — including DigiCash, Mojo Nation, ZRTP, “Zooko’s Triangle,” Tahoe-LAFS, BLAKE2 and SPHINCS. He is the founder of the Electric Coin Company and Least Authority Enterprises, which uniquely empower individuals with the right to consent about how, when and to whom their personal data is shared.', 'https://twitter.com/zooko');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (107, 'Dominik Schiener', 'Co-Founder', 'IOTA', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-dominik-schiener.jpg&w=60&h=60&q=75', null, 'https://medium.com/@domschiener', null, 'https://twitter.com/DomSchiener', null, null, 'https://www.linkedin.com/in/schiener/', null, null, 'Dominik is an Italian entrepreneur and co-founder of IOTA, where he’s focused on building the Machine Economy through Open Source Software. He has been in the Blockchain space since 2011, with his first startup ventures in Switzerland and the UK. With his work, he has won international prizes from the likes of Deloitte, Wanxiang, GTEC and Emirates NBD.', 'http://schiener.me/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (108, 'Ari Paul', 'Co-Founder & CIO', 'BlockTower Capital', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-ari-paul.jpg&w=60&h=60&q=75', null, 'https://medium.com/@AriDavidPaul', null, 'https://twitter.com/AriDavidPaul', null, null, 'https://www.linkedin.com/in/ari-paul-8895a57/', null, null, 'Ari Paul is co-founder and CIO of BlockTower Capital. He was previously a portfolio manager for the University of Chicago’s $8 billion endowment, and a derivatives market maker and proprietary trader for Susquehanna International Group (SIG).', 'https://thecryptocurrencyinvestor.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (109, 'Elizabeth Stark', 'CEO & Co-Founder', 'Lightning Labs', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/06/person-elizabeth-stark.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/starkness', null, null, 'https://www.linkedin.com/in/elizabeth-stark-361009184/', null, null, 'Elizabeth Stark is co-founder and CEO of Lightning Labs, where she is building a programmable financial layer for the internet through fast, scalable blockchain transactions. Further, she is a fellow at Coin Center, the leading digital currency policy organization, and an advisor at Chia, a company building a new blockchain protocol based on proof of space and time.', 'https://twitter.com/starkness');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (110, 'Marco Streng', 'CEO & Co-Founder', 'Genesis Group', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/08/person-marco-streng.jpg&w=60&h=60&q=75', null, 'https://medium.com/@Marco_Streng', null, 'https://twitter.com/marco_streng', null, null, 'https://www.linkedin.com/in/marco-streng-6491a67b/', null, null, 'Marco Streng is the CEO and co-founder of Genesis Group and Genesis Mining, one of the largest crypto mining companies in the world. Prior to co-founding Genesis in 2013 and becoming an impassioned advocate for blockchain technology and cryptocurrencies, Marco studied mathematics at the Ludwig-Maximilian University of Munich.', 'https://twitter.com/marco_streng');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (111, 'Jack Lu', 'Founder & CEO', 'Wanchain', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/07/person-jack-lu-1.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/jacklu_wan', null, null, 'https://www.linkedin.com/in/jack-lu-57995469/', null, null, 'Jack Lu grew up in South-East China. He studied Enterprise Management at Peking University in China and Economics & Computer Science at Ohio State University. In between, he worked in the financial industry until he transitioned to the e-commerce industry during the internet boom.', 'https://twitter.com/jacklu_wan');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (112, 'Craig Wright', 'Chief Scientist', 'nChain', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-craig-wright.jpg&w=60&h=60&q=75', null, 'https://medium.com/@craig_10243', null, null, null, null, 'https://www.linkedin.com/in/craigswright/', null, 'https://www.instagram.com/dr.craigwright/', 'Craig Steven Wright is an Australian computer scientist and businessman. He is the current Chief Scientist at nChain and the founder and figurehead of Bitcoin SV.', 'https://craigwright.net/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (113, 'Chris Larsen', 'Executive Chairman', 'Ripple', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-chris-larsen.jpg&w=60&h=60&q=75', null, 'https://medium.com/@chrislarsensf', null, 'https://twitter.com/chrislarsensf', null, null, null, null, null, 'Chris Larsen is Executive Chairman of Ripple’s board of directors and former CEO and co-founder of Ripple. Prior to Ripple, Chris co-founded and served as CEO of Prosper, a peer-to-peer lending marketplace, and E-LOAN, a publicly traded online lender.', 'https://twitter.com/chrislarsensf');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (115, 'Alex Mashinsky', 'CEO', 'Celsius Network', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-alex-mashinsky.jpg&w=60&h=60&q=75', 'http://www.mashinsky.com/md/blog.html', 'https://medium.com/@Mashinsky', null, 'https://twitter.com/Mashinsky', null, 'https://www.facebook.com/AlexMashinskyCEO', 'https://www.linkedin.com/in/mashinsky/', null, 'https://www.instagram.com/mashinsky/', 'Alex is a serial entrepreneur, founder of seven NYC based startups, raising over $1 billion and exiting over $3 billion. Alex founded two of New York City’s top 10 venture-backed exits since 2000; Arbinet with a 2004 IPO that had a market cap of over $750M and Transit Wireless currently valued at $1.2 billion. He is currently the CEO of Celsius Network.', 'http://www.mashinsky.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (116, 'Changpeng Zhao', 'CEO & Co-Founder', 'Binance', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-changpeng-zhao.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/cz_binance', null, null, 'https://www.linkedin.com/in/cpzhao/', null, 'https://www.instagram.com/changpengzhao/', 'Changpeng Zhao is a Chinese-Canadian business executive, who is the founder and CEO of Binance, the world’s largest cryptocurrency exchange by trading volume, as of April 2018. Zhao was previously a member of the team that developed Blockchain.info and also served as chief technology officer of OKCoin.', 'https://twitter.com/cz_binance');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (117, 'Cal Evans', 'Managing Associate', 'Gresham International', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2018/09/Gresham-International-team-cal-evans.jpg&w=60&h=60&q=75', null, 'https://medium.com/@calevanscrypto', null, null, null, null, 'https://www.linkedin.com/in/calevans1/', null, null, 'Cal Evans is perhaps one of the very few international technology lawyers with expertise in ICO compliance and strategy. A law graduate from London, Cal is a UK lawyer and US multi-state litigation expert. In 2015, Cal undertook his certificate in financial markets from Yale University. In 2016, Cal left a top California law firm to start Gresham International, a compliance and strategy firm specializing in the cryptocurrency space.', 'https://www.greshaminternational.net/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (118, 'Yi He', 'CMO & Co-Founder', 'Binance', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-yi-he.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/heyibinance', null, null, 'https://www.linkedin.com/in/yi-he-%E4%BD%95%E4%B8%80/?originalSubdomain=mt', null, null, 'Yi He is the co-founder and Chief Marketing Office of Binance. Previously she was a founder of OKCoin and a TV anchor for China Travel TV Channel.', 'https://twitter.com/heyibinance');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (119, 'Rune Christensen', 'CEO & Founder', 'MakerDAO', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-rune-christensen.jpg&w=60&h=60&q=75', null, 'https://medium.com/@Rune4444', null, 'https://twitter.com/runekek', null, null, 'https://www.linkedin.com/in/rune-christensen-5b5220ab/', null, null, 'Rune Christensen is Chief Executive Officer and Cofounder of MakerDAO. MakerDAO issues Dai, the world’s first stablecoin on the Ethereum blockchain, that eliminates volatility through a system of smart contracts designed to respond to market dynamics.', 'https://twitter.com/runekek');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (120, 'Steven Becker', 'President and COO', 'MakerDAO', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-steven-becker.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/altsdbecker', null, null, 'https://www.linkedin.com/in/steven-becker/', null, null, 'Steven Becker is President and COO of the organization developing MakerDao. MakerDAO is a unique dApp that generates Dai, the world’s first decentralized stablecoin that facilitates economic growth on the blockchain and economic empowerment with the blockchain.', 'https://twitter.com/altsdbecker');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (121, 'Sam Williams', 'Founder', 'Arweave', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-sam-williams.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/samecwilliams', null, null, 'https://www.linkedin.com/in/sam-williams-2b5111144/?originalSubdomain=de', null, null, 'Sam is a Ph.D. candidate with extensive experience in decentralized system design and implementation, having delivered a variety of projects including a distributed operating system. He currently serves as Co-Founder & CEO of Arweave.', 'https://twitter.com/samecwilliams');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (122, 'Arthur Breitman', 'CTO & Co-Founder', 'Tezos', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-arthur-breitman.jpg&w=60&h=60&q=75', null, 'https://medium.com/@arthurb', null, 'https://twitter.com/arthurb', null, null, null, null, null, 'Arthur was an Associate and Goldman Sachs and later Vice President at Morgan Stanley. He left in 2016 to lead the Tezos Project, a cryptocurrency aimed at solving the problem of governance within the blockchain.', 'https://twitter.com/arthurb');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (123, 'Kathleen Breitman', 'CEO & Co-Founder', 'Tezos', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-kathleen-breitman.jpg&w=60&h=60&q=75', null, 'https://medium.com/@kathleenbreit', null, 'https://twitter.com/breitwoman', null, 'https://www.facebook.com/kathleen.mccaffrey', 'https://www.linkedin.com/in/breitwoman/', null, null, 'Kathleen Breitman is the CEO of Tezos, a blockchain-based smart contract platform with an on-chain governance mechanism to coordinate and push upgrades to its network. Prior to Tezos, Kathleen was a senior strategy associate for R3, a blockchain consortium of more than 70 financial firms. She has also worked at Accenture, Bridgewater Associates, and the Wall Street Journal. She holds a degree from Cornell University.', 'https://twitter.com/breitwoman');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (124, 'Ivan Bjelajac', 'CEO & Co-Founder', 'MVP Workshop', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-ivan-bjelajac.jpg&w=60&h=60&q=75', null, 'https://medium.com/@ivan.bjelajac', null, 'https://twitter.com/instantfinality', null, null, 'https://www.linkedin.com/in/ivanbjelajac/', null, null, 'Ivan Bjelajac has been full time involved with building software products since 2001. Since then, he has had a chance to work as an engineer, tech executive and (co)founder in multiple corporations and startups while being involved in four startup exits totaling $23M.', 'https://mvpworkshop.co?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (125, 'Ethan Fast', 'CTO & Co-Founder', 'Nash', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-ethan-fast.jpg&w=60&h=60&q=75', null, 'https://medium.com/@unignorant', null, 'https://twitter.com/unignorant', 'https://www.youtube.com/nashsocial', null, 'https://www.linkedin.com/in/ethanfast/', null, null, 'Ethan Fast is a co-founder of Nash. He holds a Ph.D. in Computer Science from Stanford and has published articles in leading journals, such as Nature, CHI, EMNLP, and AAAI. He is a serial startup founder and Y Combinator alum.', 'http://ethanfast.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (126, 'Tony Tran', 'CTO & Co-Founder', 'Quadency', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2018/12/Quadency-team-Tony-Tran-2.jpg&w=60&h=60&q=75', null, null, null, null, null, null, 'https://www.linkedin.com/in/tony-tran-a059983b/', null, null, 'Tony Tran is an experienced technologist with 10+ years of experience leading/managing technical teams and creating technology in the finance, security, and crypto space. He current;y serves as the CTO of Quadency, a New York-based technology startup building digital asset trading and portfolio management tools for retail and institutional investors', 'https://github.com/toant13');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (127, 'Erik Zhang', 'Co-Founder & Core Developer', 'Neo', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-erik-zhang.jpg&w=60&h=60&q=75', null, 'https://medium.com/@neoerikzhang', null, 'https://twitter.com/neoerikzhang', null, null, 'https://www.linkedin.com/in/erik-zhang-5a6b14ab/', null, null, 'Erik is the founder and a core developer of Neo, author of the dBFT consensus mechanism. He is an expert on blockchain technology and information security, as well as a strong advocate for open source development. He leads Neo’s global developer communities to continuously innovate, apply and promote technology in the blockchain field. Currently, he is leading the development of Neo3, enhancing the developer experience, to get ready for enterprise blockchain applications.', 'https://twitter.com/neoerikzhang');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (128, 'Pat Larsen', 'CEO & Co-Founder', 'ZenLedger', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-pat-larsen.jpg&w=60&h=60&q=75', null, 'https://medium.com/@PatrickLarsen', null, 'https://twitter.com/PatrickLarsen', null, null, 'https://www.linkedin.com/in/patlarsen1/', null, null, 'Pat Larsen is the CEO and co-founder of ZenLedger, a popular cryptocurrency tax solution.', 'https://twitter.com/PatrickLarsen');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (129, 'Gregory Klumov', 'CEO & Co-Founder', 'STASIS', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-gregory-klumov.jpg&w=60&h=60&q=75', null, 'https://medium.com/@gregory.klumov', null, 'https://twitter.com/gregoryklumov', null, null, 'https://www.linkedin.com/in/kknop/', null, null, 'Gregory Klumov is a specialist in alternative asset management. Starting in IT, he founded a high-speed internet service provider called BNET at 15 before moving on to financial analyst and management roles. Klumov became active in the Bitcoin and digital as-sets space during its emergence, eventually putting together a book on their regulation targeted at state-level decision-makers (“Digital Asset Regulation: A Cross-Country Analysis”).', 'https://twitter.com/gregoryklumov');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (130, 'Jörg Molt', 'CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-jorg-molt.jpg&w=60&h=60&q=75', null, 'https://medium.com/@jrgmolt', null, 'https://twitter.com/joerg_molt', null, null, 'https://www.linkedin.com/in/j%C3%B6rg-molt-268882132/', null, 'https://www.instagram.com/jorgmolt/?hl=en', 'Jorg Molt is a German entrepreneur who claims to be the co-founder of Bitcoin and owns 250,000 BTC. There is no evidence to back up these claims. He is the author of The Bitcoin Generation and the CEO of Satoshi School.', 'https://joerg-molt.de/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (131, 'Nejc Kodrič', 'CEO & Co-Founder', 'Bitstamp', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-nejc-kodric.jpg&w=60&h=60&q=75', null, 'https://medium.com/@nejc_kodric', null, 'https://twitter.com/nejc_kodric', null, null, 'https://www.linkedin.com/in/nejc-kodri%C4%8D-29922747/', null, null, 'Since starting out in 2011 with little more than some basic computer equipment, €1,000 and a love of technology, Nejc has been instrumental in developing Bitstamp into one of the most trusted and highest volume bitcoin exchanges in the world. With offices in Luxembourg, London, and New York, Bitstamp continues to grow from strength to strength, serving its customers’ bitcoin trading requirements.', 'https://twitter.com/nejc_kodric');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (132, 'Joost Jager', 'Lightning Infrastructure Engineer', 'Lightning Labs', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-joost-jager.jpg&w=60&h=60&q=75', null, 'https://medium.com/@joost.jager', null, 'https://twitter.com/joostjgr', null, null, 'https://www.linkedin.com/in/joostjager1/?originalSubdomain=nl', null, null, 'Joost occupied diverse development roles within a broad range of companies. After a world circumnavigation by sailing boat, he dedicated several years to the development of route optimization software for ocean-going vessels. Meanwhile, Joost’s interest in algorithms kept pulling his attention to Bitcoin and Lightning, to the point that he now works on it full time. His academic background is a M.Sc. in CS with a specialization in artificial intelligence.', 'https://twitter.com/joostjgr');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (133, 'Will Harborne', 'Co-Founder & Strategy', 'DeversiFi', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-will-harborne.jpg&w=60&h=60&q=75', null, 'https://medium.com/@willharborne', null, 'https://twitter.com/will_harborne', 'https://www.youtube.com/channel/UCGWnO1pSPiMqU7NOhEGzshQ', null, 'https://www.linkedin.com/in/will-harborne/', null, null, 'Will Harborne has an Engineering degree from Cambridge University and enjoys working on visionary technology projects which can revolutionize business models as well as impact wider society. He currently serves as the Chief of Strategy and Co-Founder at DeversiFi.', 'https://twitter.com/will_harborne');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (134, 'Rosh Singh', 'CEO & Co-Founder', 'Quadency', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-rosh-singh.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/rosh2point0', null, null, 'https://linkedin.com/in/singhrosh', null, null, 'Rosh Singh is a seasoned technology entrepreneur experienced in creating innovation fintech products for FX and crypto markets. He currently is Co-Founder CEO of Quadency, a New York-based technology startup building digital asset trading and portfolio management tools for retail and institutional investors.', 'https://twitter.com/rosh2point0');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (135, 'Kyle Samani', 'Co-Founder & Managing Partner', 'Multicoin Capital', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-kyle-samani.jpg&w=60&h=60&q=75', null, 'https://medium.com/@kylesamani', null, 'https://twitter.com/KyleSamani', null, null, 'https://www.linkedin.com/in/kylesamani/', null, null, 'As a former engineer, Kyle leads technical thesis formation and diligence. He is the more outwards facing partner, owning relationships with entrepreneurs and other investors. He is widely recognized in the crypto ecosystem for his writing and system-level analysis.', 'https://twitter.com/KyleSamani');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (147, 'Gavin Anderson', 'Chief Scientist', 'Bitcoin Foundation', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-gavin-andresen.jpg&w=60&h=60&q=75', 'http://gavinthink.blogspot.com/', null, null, 'https://twitter.com/gavinandresen', null, 'https://www.facebook.com/Gavin-Andresen-182652401782694/', 'https://www.linkedin.com/in/gavin-andresen-6987971/', null, 'https://www.instagram.com/gavin.andresen/', 'Gavin Andresen is a software developer best known for his involvement with Bitcoin. He is based in Amherst, Massachusetts.', 'https://twitter.com/gavinandresen');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (136, 'Narek Gevorgyan', 'CEO & Founder', 'CoinStats', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-narek-gevorgyan.jpg&w=60&h=60&q=75', null, 'https://medium.com/@narekgevorgyan1/', null, 'https://twitter.com/narek_gevorgyan', null, 'https://www.facebook.com/profile.php?id=1524623057', 'https://www.linkedin.com/in/narekgevorgyan/', null, null, 'Born in Yerevan, Armenia, Narek Gevorgyan studied Applied Mathematics at Yerevan State University and started to work as a software engineer. He founded Inomma.com, a software development shop, helping 100+ customers, mostly from Silicon Valley, to tackle their engineering. Inomma’s customer portfolio includes companies like Vidoehancne, Clinkle or Mati.', 'https://coinstats.app?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (137, 'Micree Zhan', 'Former CEO & Co-Founder', 'Bitmain', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-micree-zhan.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/micreez', null, null, null, null, null, 'Micree Zhan is a Chinese electronics engineer and businessman. He is the co-founder and former CEO of Bitmain, the world’s largest computer chip company for cryptocurrency mining. In 2018, Hurun Report named him the richest cryptocurrency billionaire in the world. In 2019, Bloomberg ranked Zhan as the world’s 9th richest self-made billionaire aged 40 or younger, with a net worth of US$5.2 billion.', 'https://twitter.com/micreez');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (138, 'Sergey Nazarov', 'Creator', 'Chainlink', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-sergey-nazarov.jpg&w=60&h=60&q=75', null, 'https://medium.com/@sergeynazarov', null, 'https://twitter.com/sergeynazarov', null, null, 'https://www.linkedin.com/in/sergeydnazarov/', null, null, 'Sergey Nazarov began his career building peer-to-peer marketplaces, going on to the investment team at FirstMark Capital. He joined the cryptocurrency revolution in 2011 and believes it has the ability to change the way societies distribute wealth, enforce contracts, and share critical information with their citizens. He is currently the CEO of SmartContract.com and the creator of Chainlink.', 'https://twitter.com/sergeynazarov');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (139, 'Richie Lai', 'Co-Founder and CIO', 'Bittrex', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-richie-lai.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/richiela', null, null, 'https://www.linkedin.com/in/richielai', null, null, 'Richie is Co-Founder and Chief Information Officer of Bittrex. He previously served as a leader in the Amazon Information Security Team.', 'https://twitter.com/richiela');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (140, 'Matej Michalko', 'CEO & Founder', 'DECENT', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2018/10/author-matej-michalko.jpg&w=60&h=60&q=75', null, 'https://medium.com/@matmichalko', null, 'https://twitter.com/matmichalko', null, null, 'https://www.linkedin.com/in/matmichalko/', null, null, 'Matej Michalko is the Founder and CEO of DECENT, a decentralized blockchain content distribution platform that is open-source and utilizes blockchain to ensure trust and security. Matej’s first interest in online privacy was fueled while studying Computer Science at the Swiss Federal Institute of Technology (EPFL) in Lausanne where he completed a project implementing privacy protection for mobile devices at the Nokia Research Center.', 'https://twitter.com/matmichalko');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (141, 'Calvin Ayre', 'Advocate', 'Bitcoin SV', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/06/person-calvin-ayre.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/CalvinAyre', 'https://www.youtube.com/user/calvinayretablog', null, 'https://www.linkedin.com/in/calvinayre/', null, null, 'Calvin Ayre is a Canadian entrepreneur and a fierce advocate for Bitcoin SV (BSV), which according to Ayre is “the only cryptocurrency and blockchain capable of realizing the ‘Satoshi Vision’ of Bitcoin founder Satoshi Nakamoto.”', 'https://calvinayre.com?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (142, 'Farbood Nivi', 'CEO & Co-Founder', 'Coinmine', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/11/person-farbood-nivi.jpg&w=60&h=60&q=75', null, 'https://medium.com/@farbood', null, 'https://twitter.com/farbood', null, null, 'https://www.linkedin.com/in/farboodnivi/', null, null, 'Farbood Nivi, an experienced entrepreneur and product leader, kicked off his entrepreneurial career when he was 18 years old by building and selling computers to families and small businesses. His experience in technology continued when he founded Grockit, an online collaborative learning platform powered by machine learning, which was backed by Benchmark and later acquired by Kaplan.', 'https://twitter.com/farbood');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (143, 'Bobby Lee', 'CEO & Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/09/person-bobby-lee.jpg&w=60&h=60&q=75', null, 'https://medium.com/@bobbyclee', null, 'https://twitter.com/bobbyclee', null, null, 'https://www.linkedin.com/in/bobbyclee/', null, null, 'Bobby Lee is an entrepreneur in the cryptocurrency industry, and he just recently founded his second startup, Ballet, in early 2019. Ballet aims to drive global adoption of Bitcoin and cryptocurrency. It is headquartered in Las Vegas, Nevada, USA.', 'https://twitter.com/bobbyclee');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (144, 'Jeffrey Wilcke', 'Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-jeffrey-wilcke.jpg&w=60&h=60&q=75', null, 'https://medium.com/@jeff.ethereum', null, 'https://twitter.com/jeffehh', null, null, 'https://www.linkedin.com/in/jeffreywilcke/', null, null, 'Jeffrey Wilke is one of the founders of Ethereum. While Vitelik is the mind behind Ethereum, Wilke is the creator of Ethereum. He started the first implementation of Ethereum using the Go programming language in 2013 and continues to lead Ethereum development today.', 'http://jeff.grid-games.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (145, 'Anthony Di lorio', 'CEO & Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-anthony-de-lorio.jpg&w=60&h=60&q=75', null, 'https://medium.com/@diiorioanthony', null, 'https://twitter.com/diiorioanthony', null, 'https://www.facebook.com/diiorioanthony', 'https://www.linkedin.com/in/anthonydiiorio1', null, 'https://www.instagram.com/diiorioanthony/?hl=en', 'Anthony Di Iorio is a Canadian entrepreneur primarily known as a co-founder of Ethereum and an early investor in Bitcoin. Di Iorio is the founder and CEO of the blockchain company Decentral, and the associated Jaxx wallet. He also served as the first chief digital officer of the Toronto Stock Exchange. In February 2018, Forbes estimated his net worth at $750 million–$1 billion.', 'https://twitter.com/diiorioanthony');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (146, 'Hudson Jameson', 'Developer, DevOps, Comms', 'Ethereum Foundation', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-hudson-jameson.jpg&w=60&h=60&q=75', 'https://hudsonjameson.com/', 'https://medium.com/@hudsonjameson', null, 'https://twitter.com/hudsonjameson', null, null, 'https://www.linkedin.com/in/hudsonjameson/', null, null, 'Hudson Jameson is an Ethereum enthusiast and core developer liaison with a rich background in blockchain projects.', 'https://hudsonjameson.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (148, 'Pierre Rochard', 'Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-pierre-rochard.jpg&w=60&h=60&q=75', 'https://nakamotoinstitute.org/authors/pierre-rochard/', 'https://medium.com/@pierre_rochard', null, 'https://twitter.com/pierre_rochard', null, null, 'https://www.linkedin.com/in/pierrerochard/', null, null, 'Pierre Rochard has researched and written about Bitcoin since February 2013. He co-founded the Satoshi Nakamoto Institute in 2014 to curate the best primary source literature. In addition to developing Bitcoin software, Pierre is an outspoken advocate for Bitcoin’s decentralized governance. In 2017 he began co-hosting the Noded Bitcoin Podcast. Pierre is widely recognized as an authority on the investment case for Bitcoin.', 'https://rochard.org/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (149, 'Emin Gün Sirer', 'Founder & CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-emin-gun-sirer.jpg&w=60&h=60&q=75', null, 'https://medium.com/@el33th4x0r', null, 'https://twitter.com/el33th4xor', null, null, 'https://www.linkedin.com/in/emin-gun-sirer-0a921a4/', null, 'https://www.instagram.com/el33th4x0r', 'Emin Gün Sirer is a Turkish-American computer scientist. He is currently an associate professor of computer science at Cornell University, co-director of IC3. He is known for his contributions to peer-to-peer systems, operating systems, and computer networking.', 'http://hackingdistributed.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (150, 'Joseph Poon', 'Co-Founder', 'Lightning Labs', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-joseph-poon.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/jcp', null, null, null, null, null, 'Joseph Poon is the co-founder of Lightning Labs, which is implementing the Lightning Network White Paper Poon wrote to solve limitations in the Bitcoin blockchain. It’s designed to give Bitcoin trustless support for thousands of very low-cost transactions per second. In addition to overcoming Bitcoin’s scaling limitations, it adds the capability for cross-chain transactions, privacy protection, micropayments, payment receipts, and multi-hop payments.', 'https://twitter.com/jcp');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (151, 'Manish Kataria', 'COO & Co-Founder', 'Quadency', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-Manish-Kataria.jpg&w=60&h=60&q=75', null, null, null, null, null, null, 'https://www.linkedin.com/in/manish-kataria/', null, null, 'Manish Kataria is a digital strategist with focus areas including blockchain, fin-tech, innovation methodology, omnichannel marketing & commerce, and customer experience. Background includes multiple industry verticals with roles across agency, consulting, and client-side (startup and enterprise). He currently serves as co-founder and COO to Quadency.', 'https://www.linkedin.com/in/manish-kataria/');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (152, 'Jimmy Song', 'Instructor', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-jimmy-song.jpg&w=60&h=60&q=75', null, 'https://medium.com/@jimmysong', null, 'https://twitter.com/jimmysong', 'https://www.youtube.com/channel/UCEFJVYNiPp8xeIUyfaPCPQw', null, 'https://www.linkedin.com/in/jimmysong/', null, null, 'Jimmy Song is a Bitcoin Core Contributor and former VP of Engineering for Armory. Song caught the Bitcoin bug back in 2011 and started contributing to Bitcoin open source projects since 2013. He’s a contributor to CoinDesk and has a popular Bitcoin blog.', 'https://programmingbitcoin.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (153, 'Julian Kopald', 'Founder', 'Prodoge', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-Julian-Kopald.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/jayralez', null, null, 'https://www.linkedin.com/in/juliankopald/', null, null, 'Julian is into Art, Creative Business Adventures, Ideas, Strategies, and Travels. Julian bought his first bitcoins for 22 cents on the BTC-E exchange while living in Argentina and ever since has been inspired by the idea of bringing currency freedom to the world. He funded the original Bitcoin Dark Wallet, bought “TheBitcoinETF.com” and tried to start a Bitcoin ETF in 2012, before going on to release some of the first multi-currency banking apps, and now Prodoge.', 'https://realspartan.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (154, 'Kristina Lucrezia Cornèr', 'Managing Editor', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-Kristina-Lucrezia.jpg&w=60&h=60&q=75', 'https://cointelegraph.com/authors/kristina-lucrezia-corner', null, null, 'https://twitter.com/KristinaLCorner', null, 'https://www.facebook.com/kristinalucrezia', 'https://www.linkedin.com/in/kristina-lucrezia-corn%C3%A8r-816703154/', null, 'https://www.instagram.com/ferouza', 'Kristina Lucrezia Cornèr is managing editor and head of features at Cointelegraph. With an M.A. in political science and classical philology, she is a passionate communicator promoting innovations, translating them into simple languages and helping visionaries to be heard.', 'https://twitter.com/KristinaLCorner');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (155, 'Jared Tate', 'Founder', 'DigiByte', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-jared-tate.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/jaredctate', null, null, 'https://www.linkedin.com/in/jaredctate/', null, null, 'Jared Tate is the founder and creator of the DigiByte blockchain. DigiByte is a public, rapidly growing and highly decentralized blockchain. DigiBytes are digital assets that cannot be destroyed, counterfeited or hacked, making them ideal for protecting objects of value like currency, information, property or important digital data.', 'https://twitter.com/jaredctate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (156, 'Steve Ehrlich', 'CEO', 'Voyager Token', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-steve-ehrlich.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/ehrls15', null, null, 'https://www.linkedin.com/in/stephenehrlich/', null, null, 'Stephen Ehrlich is a veteran in the capital markets space. He has worked in the industry for more than 25 years, starting his career at TIR Securities, an institutional brokerage that was sold to E*TRADE Financial. Mr. Ehrlich was asked to join E*TRADE Financial and spent seven years at the company, ultimately rising to CEO of E*Trade Professional Trading, which later became LightSpeed Financial. Under Mr. Ehrlich’s lead, Lightspeed Financial became the third-largest brokerage in the United States and executed an average of 450,000 trades per day by 2009.', 'https://twitter.com/ehrls15');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (157, 'Ivan Liljeqvist', 'Host', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-Ivan-Liljeqvist.jpg&w=60&h=60&q=75', null, 'https://medium.com/@ivanliljeqvist', null, 'https://twitter.com/IvanLiljeqvist', 'https://www.youtube.com/user/LiljeqvistIvan', 'https://www.facebook.com/ivan.liljeqvist', 'https://www.linkedin.com/in/ivanliljeqvist/', 'https://t.me/ivanontech', 'https://www.instagram.com/ivanontech/', 'Ivan Liljeqvist is an international speaker, blockchain educator, software developer, and data scientist. Ivan’s Youtube channel, Ivan on Tech, is a truly global phenomenon spreading knowledge about blockchain technology to hundreds of thousands of viewers all over the world. Ivan makes the complex simple by delivering entertaining and exciting talks.', 'https://twitter.com/IvanLiljeqvist');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (158, 'Virgil Griffith', 'Research Scientist', 'Ethereum Foundation', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-virgil-griffith.jpg&w=60&h=60&q=75', null, 'https://medium.com/@virgilgr/', null, 'https://twitter.com/virgilgr', null, 'http://www.facebook.com/10107420255220919', 'https://www.linkedin.com/in/virgilgr/', null, null, 'Virgil Griffith is an American programmer, known for being the creator of WikiScanner. He has published papers on artificial life and integrated information theory. In developing WikiScanner, Griffith described his mission as “to create minor public-relations disasters for companies and organizations I dislike.” Griffith was arrested in 2019 for giving an Ethereum related presentation in North Korea.', 'https://virgil.gr/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (159, 'Dae Yu', 'Co-Host & Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-dae-yu.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/LuggageDonkey', 'https://www.youtube.com/channel/UCbsnhul3AeUV7ByvFMMyWQQ', 'https://www.facebook.com/windshieldtime21/', 'https://www.linkedin.com/company/windshieldtime', null, null, 'Dae Yu completed an undergraduate degree in Electrical Engineering.  Afterward, he kicked off a career as a Network Engineer that morphed into a Sales Engineering.', 'https://www.windshieldtime.studio/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (160, 'Brad Garlinghouse', 'CEO', 'Ripple', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-brad-garlinghouse.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/bgarlinghouse', null, 'https://www.facebook.com/bgarlinghouse', 'https://www.linkedin.com/in/bradgarlinghouse/', null, null, 'Brad Garlinghouse is CEO of the financial technology company Ripple, which builds financial products for the XRP cryptocurrency. He was formerly the CEO and Chairman of Hightail (formerly YouSendIt), the file-sharing site.', 'https://twitter.com/bgarlinghouse');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (161, 'Riccardo Spagni', 'Former Project Lead', 'Monero', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-riccardo-spagni.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/fluffypony', null, null, null, null, null, 'Riccardo Spagni is a member of Monero Core Team, who stewards of the Monero Project, and is also the lead maintainer of the Monero project. With an academic background in informatics and logistics. Ricccardo spent many years in software development, before starting a business in the import/export industry with his wife.', 'https://twitter.com/fluffypony');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (162, 'Richard Heart', 'Founder', 'HEX', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-richard-heart-1.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/RichardHeartWin', 'https://www.youtube.com/channel/UCta3TYFhzfpPvOtKBDifYJA', null, null, null, null, 'Richard Heart is a Bitcoin, blockchain, cryptocurrency, and internet marketing expert who has founded and managed several successful Internet startups, the largest having 150 employees with a turnover of $60M and operating in mortgage and consumer finance markets.', 'https://twitter.com/RichardHeartWin');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (163, 'Roger Ver', 'Executive Chairman', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-roger-ver-1.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/rogerkver', null, 'https://www.facebook.com/rogerkver', 'https://www.linkedin.com/in/rogerver', null, null, 'Roger Ver is an early investor in bitcoin-related startups. He has been a prominent supporter of bitcoin adoption and saw bitcoin as a means to promote economic freedom. He now promotes Bitcoin Cash, a hard fork of the cryptocurrency created with the intent to fix issues such as high fees and long transaction confirmation times.', 'https://rogerver.com/?utm_source=cryptoslate');
