INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (194, 'Mark Zuckerberg', 'CEO & Founder', 'Meta', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-mark-zuckerberg.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/finkd', null, 'https://www.facebook.com/zuck', null, null, 'https://www.instagram.com/zuck/', 'Mark Elliot Zuckerberg is an American programmer, Internet entrepreneur, executive, and philanthropist. He is the chairman, chief executive officer, and co-founder of social networking website Facebook. His net worth is estimated to be US$54.5 billion as of September 2016, ranking him as the 5th richest person in the world.', 'https://twitter.com/finkd');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (195, 'Samson Mow', 'Chief Strategy Officer', 'Blockstream', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/07/person-samson-mow.jpg&w=60&h=60&q=75', null, 'https://medium.com/@Excellion', null, 'https://twitter.com/Excellion', null, 'https://www.facebook.com/samson.mow', 'https://www.linkedin.com/in/samson/?originalSubdomain=cn', null, null, 'Samson Mow is Blockstream’s CSO and Pixelmatic’s CEO. Blockstream is the leading provider of blockchain technologies, on the forefront of work in cryptography and distributed systems. Samson founded Pixelmatic in 2011 to creating engaging games that are truly social and encourage new connections to be made.', 'https://www.excellion.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (196, 'Lior Yaffe', 'Co-Founder and Director', 'Jelurida', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/02/person-lior-yaffe.jpg&w=60&h=60&q=75', null, 'https://medium.com/@lyaffe', null, 'https://twitter.com/lioryaffe', null, null, 'https://www.linkedin.com/in/lior-yaffe-61106b1/?originalSubdomain=il', null, null, 'Lior Yaffe is the Co-Founder and Director of Jelurida and has 20+ years of experience in design, development, and deployment of enterprise applications for large organizations. Lior has his B.A. in computer science from the Technion in Haifa, Israel. Lior is a Senior Java Developer for the Nxt and Ardor Blockchain Platforms.', 'https://twitter.com/lioryaffe');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (197, 'Jed McCaleb', 'Co-Founder', 'Stellar', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-jed-mccaleb-1.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/jedmccaleb', null, null, 'https://www.linkedin.com/in/jed-mccaleb-4052a4', null, 'https://www.instagram.com/jedmccaleb/', 'Jed McCaleb is co-founder and Chief Architect of the Stellar Development Foundation, an open network that connects the world’s financial infrastructure. In 2014, Jed created Stellar, an open-source blockchain network built to democratize economic participation by making money more fluid, markets more open, and people more empowered. An early pioneer in decentralized systems and blockchain technology, he created eDonkey2000 which became one of the largest file-sharing networks in its time.', 'https://twitter.com/jedmccaleb');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (198, 'Andreas Antonopoulos', 'Author', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-andreas-antonopoulos.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/aantonop', 'https://youtube.com/aantonop', 'https://www.facebook.com/AndreasMAntonopoulos/', 'https://www.linkedin.com/in/amantonopoulos/', null, null, 'Andreas Antonopoulos is a best-selling author, speaker, educator, and one of the world’s foremost bitcoin and open blockchain experts. He is known for delivering electric talks that combine economics, psychology, technology, and game theory with current events, personal anecdote, and historical precedent effortlessly transliterating the complex issues of blockchain technology out of the abstract and into the real world.', 'https://www.patreon.com/aantonop?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (199, 'Balaji Srinivasan', 'Co-Founder and Board Member', 'Coin Center', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-balaji-srinivasan-1.jpg&w=60&h=60&q=75', null, 'https://medium.com/@balajis', null, 'https://twitter.com/balajis', null, null, 'https://www.linkedin.com/in/balajissrinivasan/', null, null, 'Balaji Srinivasan is the former CTO of Coinbase and co-founder of Counsyl (acquired by Myriad), Earn.com (acquired by Coinbase), Teleport (acquired by Topia), and a board member of Coin Center.', 'https://balajis.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (200, 'Simon Yu', 'CEO & Co-Founder', 'StormX', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-simon-yu.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/SimonYuSEA', null, null, 'https://www.linkedin.com/in/simonyu0311/', null, null, 'CEO and Co-Founder of Storm, Simon’s own personal experience with financial hardship and the ‘gig economy’ during college, combined with his passion for blockchain, helped spark Storm’s vision — to empower the lives of billions worldwide by allowing anyone to earn anywhere, anytime, from any device.', 'https://twitter.com/SimonYuSEA');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (201, 'Abdalla Kablan', 'Executive Chairman', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/07/person-abdalla-kablan.jpg&w=60&h=60&q=75', null, 'https://medium.com/@DrKablan', null, 'https://twitter.com/DrKablan', null, 'https://www.facebook.com/abdalla.kablan', 'https://www.linkedin.com/in/drabdallakablan/', null, null, 'Throughout his career, Abdalla Kablan founded a number of startups and companies specializing in deep learning, professional matchmaking, and Fintech. Furthermore, Dr. Kablans is an Alumni of Microsoft Ventures, UK and worked on projects, which were featured at the World Economic Forum 2017 in Davos. Dr. Kablan is also an academic at the University of Malta where he lectures and researches topics related to computational intelligence, financial engineering, and financial data science.', 'https://twitter.com/DrKablan');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (202, 'Josh Rager', 'Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-josh-rager.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/Josh_Rager', null, null, 'https://www.linkedin.com/in/joshrager/', 'https://cryptoslate.com/people/josh-rager/', null, 'Josh Rager is a respected cryptocurrency analyst, trader, and the Co-Founder of BlockRoots, a platform to learn about blockchain and cryptocurrency. Rager is also a Senior Advisor for Level and on the Trader Advisory Board for Glassnode.', 'https://twitter.com/Josh_Rager');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (203, 'Gavin Wood', 'Founder / Lead Developer', 'Parity Technologies', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-gavin-wood.jpg&w=60&h=60&q=75', null, 'https://medium.com/@gavofyork', null, 'https://twitter.com/gavofyork', null, null, 'https://www.linkedin.com/in/gavin-wood-88843316/', null, null, 'Gavin Wood was the Chief Technology Officer and co-­founder of the Ethereum Project. He is the co-designer of the Ethereum Protocol, author of its formal specification, and is the creator and author of the first functional Ethereum implementation. Gavin Wood designed and stewarded the Solidity language, was the project chief of the IDE, and designed and implemented the Whisper protocol. He has pushed “state-of­-the-art” in analysis tools and programming languages, as well as co­-founded several technology start­ups. Gavin Wood has presented to numerous audiences around the world, from keynotes at regional technology conferences, to musings on the future of legal systems at Harvard.', 'https://gavwood.com/?utm_source=cryptoslate');
