INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (206, 'W. Scott Stornetta', 'Chief Scientist', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-scott-stornetta.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/strangethingitl', null, 'https://www.facebook.com/scottstornetta', 'https://www.linkedin.com/in/w-scott-stornetta-25186120/', null, null, 'Scott Stornetta is without a doubt, one of the founding fathers of blockchain technology. He is also a notable figure in the world of cryptography and distributed computing. The first mention of a blockchain architecture was from a publication he co-authored which described a digital hierarchy system known as a “block chain” that utilized digital time-stamps for ordering transactions.', 'https://nakamotoinstitute.org/authors/w-scott-stornetta/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (207, 'J. Christopher Giancarlo', 'Former Chairman', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/10/person-chris-giancarlo.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/giancarloMKTS', null, null, 'https://www.linkedin.com/in/j-christopher-giancarlo-77257111/', null, null, 'Christopher “Chris” Giancarlo was unanimously confirmed to serve as Chairman of the U.S. Commodity Futures Trading Commission by the U.S. Senate on August 3, 2017. Prior to becoming Chairman, Mr. Giancarlo was designated Acting Chairman on January 20, 2017, and was nominated by President Trump to serve as the Chairman on March 14, 2017, to a term that expires in April 2019. Mr. Giancarlo had served as a CFTC Commissioner since his swearing-in on June 16, 2014, after a unanimous consent by the U.S. Senate on June 3, 2014. He was nominated by President Obama on August 1, 2013.', 'https://twitter.com/giancarloMKTS');
