INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (209, 'Joe Roets', 'Chief Architect, Founder & CEO', 'Dragonchain', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/05/person-joe-roets-1.jpg&w=60&h=60&q=75', null, 'https://medium.com/@joe_91999', null, 'https://twitter.com/j0j0r0', null, null, 'https://www.linkedin.com/in/j0j0r0/', null, null, 'Joe Roets is the Architect, Founder, and Chief Executive Officer of Seattle-based Dragonchain. He has over 20 years of experience in software architecture, focused primarily on security and scalability. He has created and led multiple technology startups, beginning in the mid-1990s.', 'https://twitter.com/j0j0r0');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (210, 'Carlos Domingo', 'CEO & Founder', 'Securitize', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-carlos-domingo.jpg&w=60&h=60&q=75', null, 'https://medium.com/@carlosdomingo', null, 'https://twitter.com/carlosdomingo', null, 'https://www.facebook.com/Carlosdomingo', 'https://www.linkedin.com/in/carlosdomingo/', null, 'https://www.instagram.com/carlosdomingo', 'Carlos is co-founder and managing partner of SPiCE VC, a truly liquid, transparent and inclusive VC thanks to advances in blockchain technologies. SPiCE VC invests in startups via shares and ICOs and focuses on the post-seed, pre-Series A/pre-ICO companies in hot and disruptive technologies (blockchain, AR/VR, AI/ML, etc.). Carlos is also the founder and CEO of Securitize.', 'https://about.me/carlosdomingo?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (211, 'Dan Tapiero', 'Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/06/person-dan-tapiero.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/DTAPCAP', null, null, 'https://www.linkedin.com/in/dan-tapiero-22b41b191/', null, null, 'Dan is a veteran Global Macro investor and a co-founder of GBI. In his 20 year career, he has worked as a portfolio manager and analyst with a number of well-known investors such as Stan Druckenmiller (Duquesne Capital), Michael Steinhardt (Steinhardt Capital), Julian Robertson (Tiger Management), Steve Cohen (SAC Capital), and Lord Rothschild (RIT Capital).', 'https://bullioninternational.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (212, 'Lili Zhao', 'Director of Neo Global Development', 'Neo Foundation', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-lili-zhao.jpg&w=60&h=60&q=75', null, null, null, null, null, null, 'https://www.linkedin.com/in/lili-zhao-zurich/?originalSubdomain=ch', null, null, 'Lili Zhao is the director of Neo Global Development, the operational arm of the open-source and community-driven Neo blockchain. Additionally, she is the creator of a non-profit passion project which is devoted to the humanity of digital being. Lili is very excited to unveil the name and ethos of the project at the BlockDown conference and look forward to nurturing it with the community. Lili held board positions at both public-listed companies and non-profit organizations.', 'https://www.linkedin.com/in/lili-zhao-zurich/?originalSubdomain=ch');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (213, 'Preston Pysh', 'Show Host & Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-preston-pysh.jpg&w=60&h=60&q=75', null, 'https://medium.com/@PrestonPysh', null, 'https://twitter.com/PrestonPysh', 'https://www.youtube.com/user/BuffettsBooks', null, 'https://www.linkedin.com/in/prestonpysh', null, null, 'Preston Pysh is a graduate of West Point and Johns Hopkins University. He’s the founder of BuffettsBooks.com, and his videos on financial investing have been viewed by millions of people around the world. He takes great pleasure in taking complex ideas and making them accessible. He is the founder of the Pylon Holding Company and enjoys spending time with his wonderful family.', 'https://www.theinvestorspodcast.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (214, 'Udi Wertheimer', 'Co-Host', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/perosn-udi-wertheimer.jpg&w=60&h=60&q=75', null, 'https://medium.com/@udiwertheimer', null, 'https://twitter.com/udiWertheimer', null, null, 'https://www.linkedin.com/in/udiwertheimer/?originalSubdomain=il', null, null, 'Udi Wertheimer started his career in 2004 as a Communications Officer in the Israel Defense Forces. From 2010-2011, he was a Developer at EV-Plus. From 2011 to 2014 he worked as a Ruby on Rails Developer at FTBpro. Later, he worked as a Full Stack Software Engineer at eBay and PayPal. From 2016-2017, he was a Blockchain Research Lead at Colu. Since 2017, Udi has been an Independent Developer and Consultant, Bitcoin Activist and Blockchain Skeptic.', 'http://www.recklessreview.horse/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (215, 'Raoul Pal', 'CEO & Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-raoul-pal.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/RaoulGMI', 'https://www.youtube.com/channel/UCBH5VZE_Y4F3CMcPIzPEB5A', null, 'https://www.linkedin.com/in/raoul-pal-real-vision/', null, null, 'A former hedge fund manager who retired at 36, Raoul Pal is a co-founder of Real Vision, a financial media company offering in-depth video interviews and research publications from the world’s best investors. He has run a successful global macro hedge fund, co-managed Goldman Sachs’ hedge fund sales business in Equities and Equity Derivatives in Europe, and helped design the BBC TV program Million Dollar Traders, training participants in investment and risk management strategy.', 'https://www.realvision.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (216, 'Loi Luu', 'CEO & Co-Founder', 'Kyber Network', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-loi-luu.jpg&w=60&h=60&q=75', null, 'https://medium.com/@loiluu', null, 'https://twitter.com/loi_luu', null, null, 'https://www.linkedin.com/in/loiluu/?originalSubdomain=sg', null, null, 'Loi Luu has spent many years as an industry researcher working on cryptocurrencies, smart contract security, and distributed consensus algorithms focusing on several problems of digital assets from improving the security to enhancing the scalability and usability of public cryptocurrencies. To date, his work benefits many blockchain companies and projects worldwide and collectively these companies are worth more than tens of billions of US dollars. Luu currently serves as the CEO and co-founder of Kyber Network.', 'https://loiluu.com/?utm_source=cryptoslate');
