INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (218, 'Mariana Gospodinova', 'General Manager, Europe', 'Crypto.com', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-mariana-gospodinova.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/mariamoita', null, null, null, null, null, 'Mariana Gospodinova is the General Manager of Europe for Crypto.com. In her previous role as COO of Binance Europe, she headed the operations in Malta and looked after the successful launch of the crypto to fiat exchange and other local initiatives. Mariana has solid experience in the payment industry in Europe, with leadership roles in operations at companies including Paysafe and SysPay, focusing on building up a payment platform in line with regulatory and card scheme requirements.', 'https://twitter.com/mariamoita');
