INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (221, 'On Yavin', 'CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-on-yavin.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/on_yavin', 'https://www.youtube.com/channel/UCOiiH2Xfcj-aHooP2DnwbCg', 'https://www.facebook.com/on.yavin', 'https://www.linkedin.com/in/onyavin/', 'https://t.me/onyavin', null, 'On Yavin is the Founder and CEO of Cointelligence and the blockchain and cryptocurrency education platform Cointelligence Academy. Since 2017, he has been dedicated to championing cryptocurrency and the blockchain. On works tirelessly to build the cryptocurrency community, bank the unbanked, fight corruption, and make education accessible to people around the world.', 'https://www.cointelligence.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (222, 'Jake Yocom-Piatt', 'Project Lead', 'Decred', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-jake-yocom-piatt.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/behindtext', null, null, 'https://www.linkedin.com/in/jacob-yocom-piatt-a703006a/', null, null, 'Jake Yocom-Piatt is Project Lead for Decred,  cryptocurrency pioneer who has been using, funding and directing the creation of open-source software for 10+ years, he has been an expert source for the Chicago Tribune and a variety of industry publications. He has also been a frequent panelist and has taught workshops on cryptocurrency governance.', 'https://twitter.com/behindtext');
