INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (224, 'Chieh Liu', 'CEO & Founder', 'Overbit', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-chieh-liu.jpg&w=60&h=60&q=75', 'https://blog.overbit.com/en/', null, null, 'https://twitter.com/cl_overbit', null, null, 'https://www.linkedin.com/in/chieh-liu-07290b157/?originalSubdomain=hk', 'https://t.me/chiehliu', null, 'Chieh Liu developed a strong interest in blockchain and cryptocurrency while he was studying computer science at California State Polytechnic University in 2011. After graduating, he worked as an advisor for several financial investment firms across several Asian markets for almost a decade.', 'https://twitter.com/cl_overbit');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (225, 'Thomas Thornton', 'President', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/04/person-thomas-thornton.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/tommythornton', null, null, 'https://www.linkedin.com/in/thomas-thornton-16130310/', null, null, 'Thomas Thornton is a former portfolio manager, senior trader, and technical analyst with Level Global Investors and Galileo Capital. Thornton has written a daily market note for a select group of hedge fund managers for years and now has offered it for all investors with Hedge Fund Telemetry. His long term focus on sentiment indicators borders on the obsessive. The growing team at Hedge Fund Telemetry is comprised of current and former buy and sell-side individuals.', 'https://www.hedgefundtelemetry.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (226, 'Carylyne Chan', 'Interim CEO & Chief Strategy Officer', 'CoinMarketCap', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/04/person-carylyne-chan.jpg&w=60&h=60&q=75', null, 'https://medium.com/@carylyne', null, 'https://twitter.com/carylyne', null, null, 'https://www.linkedin.com/in/carylynechan/', null, null, 'Carylyne Chan is the Interim CEO and Chief Strategy Officer of CoinMarketCap, where she defines the vision and initiatives the company pursues. She also heads marketing at CoinMarketCap, where she leads the company on execution, branding, and growth. She previously co-founded an AI platform (acquired), built machine learning and natural language processing models for extractive AI summarization, and has developed and launched new products in markets like US, Hong Kong, India and Singapore with American Express, 3M and AGT. She is also probably one of the biggest oolong tea enthusiasts you will meet in daily life.', 'https://twitter.com/carylyne');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (227, 'Ross Ulbricht', 'Creator', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/04/person-ross-ulbricht.jpg&w=60&h=60&q=75', null, 'https://medium.com/@RossUlbricht', null, 'https://twitter.com/realrossu', 'https://www.youtube.com/channel/UCJHvrjMcj4H0Vq0ysrSJasA', 'https://www.facebook.com/freerossulbricht/', 'https://www.linkedin.com/in/rossulbricht/', null, 'https://www.instagram.com/freerossulbricht/', 'Ross William Ulbricht (born March 27, 1984) is a convicted American darknet market operator and narcotics trafficker, best known for creating and running the Silk Road website from 2011 until his arrest in 2013. He was known under the pseudonym “Dread Pirate Roberts,” after the fictional character in the novel The Princess Bride (1973) and its 1987 film adaptation. Ulbricht was convicted of money laundering, computer hacking, conspiracy to traffic fraudulent identity documents, and conspiracy to traffic narcotics by means of the Internet in February 2015.  He is currently serving a double life sentence plus forty years without the possibility of parole. The U.S. Court of Appeals for the Second Circuit upheld the conviction and sentence in May 2017. The Supreme Court of the United States declined to hear a further appeal in June 2018.  The Maryland prosecution dismissed with prejudice a five-year-old pending indictment in July 2018.', 'https://twitter.com/realrossu');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (228, 'Sam Bankman-Fried', 'CEO', 'FTX', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/04/person-sam-bankman-fried.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/SBF_Alameda', null, null, 'https://www.linkedin.com/in/sam-bankman-fried-8367a346/', null, null, 'Sam Bankman-Fried is the CEO of crypto derivatives exchange FTX and Alameda Research. He majored in Physics at the Massachusetts Institute of Technology and graduated in 2014.', 'https://twitter.com/SBF_Alameda');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (229, 'Lynn Liss', 'COO & Co-Founder', 'Akoin', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/04/person-lynn-liss.jpg&w=60&h=60&q=75', null, 'https://medium.com/@lynnliss', null, null, null, null, 'https://www.linkedin.com/in/lynnliss/', null, null, 'Social Impact entrepreneur, operating executive, driving financial revolutions and related technology, partnerships and business models. Lynn has over 10+ years in the social impact sector; advising, executing and operating innovating business models for world change. An early mover in the ‘blockchain for good’ space, Lynn was a founding partner at ICO Impact Group, providing advisement to blockchain companies and new cryptocurrencies with a social impact. Over 10+ years as an executive consultant with leading firms BearingPoint and CGI-AMS providing strategic advisement and deploying innovative technology with Fortune 500s, including negotiating a $20M+ deal signed by the COO of Microsoft. Combined with an MBA in Strategy/Finance from the University of Chicago, Lynn has deep networks and experience across the startup, venture capital, entertainment, finance, technology, and social good sectors.', 'https://medium.com/@lynnliss');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (230, 'Jon Karas', 'President & Co-Founder', 'Akoin', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/04/person-jon-karas.jpg&w=60&h=60&q=75', null, null, null, null, null, null, 'https://www.linkedin.com/in/jokaras/', null, null, 'Finance, Entertainment and Technology industry entrepreneur, seasoned capital formation leader, film producer, and talent representative. With over 25 years in the entertainment and finance industries, Jon is a major force at the intersection of celebrity and social good. He is a founding Advisory Board member of Slate Entertainment Group, creator of disruptive crypto-driven streaming and ticketing platforms. As a veteran of the William Morris Agency, he founded Infinity Management International in 1990, which represents top record producers, film and television writers, directors, and producers. He began his career practicing transactional and securities law at two major law firms on Park Avenue in New York.', 'https://www.linkedin.com/in/jokaras/');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (231, 'Jay Hao', 'CEO', 'OKX', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/03/person-jay-hao.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/JayHao8', null, null, null, null, null, 'Jay is a tech veteran and seasoned industry leader. Prior to OKEx, he focused on blockchain-driven applications for live video streaming and mobile gaming. Before tapping into the blockchain industry, he already had 21 years of solid experience in the semiconductor industry, developing a wide range of UVLED, ASIC, FPGA, multimedia codec, as well as complex system-on-chip (SOC) design for multimedia processors and wireless communication systems. He is also a recognized leader with successful experiences in product management.', 'https://twitter.com/JayHao8');
