INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (233, 'Joel Edgerton', 'COO', 'bitFlyer USA', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/06/person-joel-edgerton.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/Joel_bitFlyer', null, null, 'https://www.linkedin.com/in/joel-edgerton-2bb197/', null, null, 'Joel Edgerton is COO at bitFlyer USA, a global cryptocurrency exchange with offices in Tokyo, San Francisco, and Luxembourg. Joel leads the company’s expansion in the USA, developing a strong team with institutional and retail customer experience. He has advocated for cryptocurrency exchanges to go beyond simply offering products, to better protect customer interests and solve customer problems.', 'https://twitter.com/Joel_bitFlyer');
