INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (235, 'Shunsuke Murasaki', 'CEO of EMURGO Indonesia', 'EMURGO', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/06/person-shunsuke-murasaki.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/encrypt2', null, null, 'https://www.linkedin.com/in/shunsuke-murasaki-942012ba/?originalSubdomain=id', null, null, 'As CEO of EMURGO Indonesia, Shunsuke Murasaki leads EMURGO Group’s local Indonesian unit to drive the regional adoption of blockchain solutions in the public and private sectors. Having garnered mutual collaboration with some of Indonesia’s leading local universities, EMURGO Indonesia places a core emphasis on blockchain education and exploring real blockchain use cases to empower citizens to provide for greater economic opportunities. Murasaki has also played a leading role in the launch and integration of EMURGO Traceability Solution in partnership with a local coffee brand. Murasaki-san has worked at multiple firms, with more than 8 years of experience in Asian markets specializing in B2B overseas sales and business development.', 'https://twitter.com/encrypt2');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (236, 'Pavel Bains', 'CEO', 'Bluezelle', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/06/person-pavel-bains.jpg&w=60&h=60&q=75', null, 'https://medium.com/@pavelbains', null, 'https://twitter.com/pavelbains', null, null, 'https://www.linkedin.com/in/pavelbains/', null, null, 'Pavel Bains is the CEO of Bluzelle – the decentralized database for the new internet. Pavel is an expert in digital media having worked with Disney, Microsoft, Warners Bros, DreamWorks. Pavel is also a frequent contributor to Forbes, Huffington Post and Fast Company, writing articles in the areas of finance and digital media. Named Technology Pioneer by the World Economic Forum.', 'https://twitter.com/pavelbains');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (237, 'Craig Cobb', 'Managing Director', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2019/12/person-Craig-Cobb.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/TraderCobb', 'https://tradercobb.com/youtube', 'https://tradercobb.com/facebook', 'https://www.linkedin.com/in/craig-cobb-tradercobb/', 'https://t.me/TraderCobb', 'https://www.instagram.com/tradercobb/', 'Craig Cobb (“TraderCobb”) is regarded as one of the world’s top trading educators in cryptocurrency. His easy to follow checklist based strategies means that anyone from a complete beginner can learn to trade like a professional. TraderCobb has spent the last 13-years traveling around the world, presented to over 100,000 people and is proud to say has made some amazing friendships with the world’s best traders who he calls mentors along the way.', 'https://tradercobb.com?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (239, 'Nathan Batchelor', 'Lead Bitcoin Analyst SIMETRI', 'Crypto Briefing', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/10/person-nathan-batchelor-photo.jpg&w=60&h=60&q=75', 'https://cryptobriefing.com/team/nathan-batchelor/', null, null, null, null, null, 'https://www.linkedin.com/in/nathan-batchelor-1689288a/?originalSubdomain=uk', null, null, 'Nathan is the lead Bitcoin analyst for SIMETRI. Nathan specializes in technical analysis. He has nearly fifteen years of experience trading and previously worked for a leading hedge fund in the United Kingdom. Prior to moving into the cryptocurrency space, Nathan specialized in the foreign exchange sector.', 'https://cryptobriefing.com/team/nathan-batchelor/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (240, 'Eugene Loza', 'CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/09/eugene-loza-photo-1.jpg&w=60&h=60&q=75', 'https://www.tradingview.com/u/EXCAVO/', null, null, 'https://twitter.com/EXCAVO', 'https://www.youtube.com/channel/UCxBUeyObDhjqVXJgGv_BqWg', 'https://www.facebook.com/Excavo', 'https://www.linkedin.com/showcase/excavo-1/', 'https://t.me/excavochannel', 'https://www.instagram.com/excavo/?hl=ru', 'Eugene Loza is the CEO of EXCAVO. EXCAVO is a company of independent financial analysts, traders, asset managers, economists and IT-developers. He is an independent financial analyst, professional trader, top-1 trader by Tradingview.com, the most popular trader by Bitcoin.com, and a top-5 Advisor Blockchain projects by ICOholder.', 'https://twitter.com/EXCAVO');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (241, 'Chun Wang', 'CEO & Founder', 'stakefish', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/09/chun-wang-photo.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/satofishi', 'https://www.youtube.com/user/yaohua2000', 'https://www.facebook.com/wangchun', 'https://www.linkedin.com/in/wangchun/', null, null, 'Chun Wang has been around this space ever since coming across someone discussing “Bitcoin” on Slashdot in 2011. He started to mine with machines purchased at the local market. Then co-founded one of the biggest mining pools, f2pool, in 2013. With the rise of Proof of Stake blockchains, he founded stake.fish in 2018 to support a broader range of projects in the blockchain ecosystem.', 'https://twitter.com/satofishi');
