INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (305, 'Cynthia Huang', 'Growth Lead', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2021/11/cynthia-huang-person.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/cynb3ar', null, null, 'https://ca.linkedin.com/in/cynthiajhuang', null, null, 'Cynthia Huang is the Growth Lead for Dtravel. She’s also the co-founder of Altcoin Fantasy, a crypto trading simulation game with 300K+ users worldwide and Airhosts Forum, the #1 Airbnb host community globally. Cynthia has won numerous awards for her projects, totaling over $100K USD in prizes and grants. She’s experienced in growth, product, and marketing. Her published works appear on sites like Hacker Noon, The Startup and CoinMarketCap and she’s been featured in interviews for publications such as Thrive Global and Authority Mag.', 'https://dtravel.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (306, 'Monica Durga', 'Founder & CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2021/11/monica-durga-person.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/0xmonica', null, null, 'https://www.linkedin.com/in/monica-durga/', null, null, 'Monica is the founder and CEO at xHashtag and has over four years of experience in the crypto space. She led several blockchain projects including building DEXs and CEX (derivatives). Her recent work includes positions at Router Protocol and Dfyn Network where she was their Chief Technical Architect as well as the founding member of the team.', 'https://xhashtag.io?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (307, 'Emile', 'CEO & Co-Founder', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2021/11/emile-xdefi-ceo.jpg&w=60&h=60&q=75', null, 'https://xdefi-io.medium.com/', null, 'https://twitter.com/xdefi_wallet', null, null, null, null, null, 'Émile was in traditional finance working for Bloomberg and Reuters before moving into tech. He launched XDEFI Wallet with co-founder David in August 2020 whom he met after being hired by BCG to participate in the launch of Blockchain ventures for the De Beers group. Since then, XDEFI Wallet has grown from a team of four to around 35 and has a user base of over 70k.', 'https://www.xdefi.io/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (308, 'Peng Zhong', 'CEO', 'Ignite', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2021/12/peng-zhong-author.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/zcpeng', null, null, 'https://www.linkedin.com/in/nylira/', null, null, 'Peng Zhong is CEO of Tendermint, leading a world-class team that empowers people to create a more transparent and accountable world through blockchain technology. Peng was the first employee at Tendermint, joining as Chief Design Officer in 2015 before becoming CEO in May 2020. With a background in interaction design and front-end engineering, Peng focuses on guiding blockchain technology across the chasm to the mainstream.', 'https://twitter.com/zcpeng');
