INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (311, 'Michael Saylor', 'Chairman & CEO', 'MicroStrategy', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/10/michael-saylor-photo.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/saylor', null, null, 'https://www.linkedin.com/in/mjsaylor/', null, null, 'Michael Saylor is the founder and principal shareholder of MicroStrategy. Since the inception of the firm, he has served as the Chairman and Chief Executive Officer of the company. Saylor entered the business intelligence marketplace with the MicroStrategy 2.0 product in 1994. At the time, it was one of the first products for building analytical applications on large relational databases. Over time, he improved this product to become an entire suite of business intelligence tools for reporting, analytical processing, and information distribution. Saylor launched the latest version of the software (MicroStrategy 2020) in December.', 'https://twitter.com/saylor');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (312, 'Nicholas Fett', 'CTO', 'Tellor', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2020/05/person-nicholas-fett.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/themandalore9', 'https://www.youtube.com/channel/UCJteW6bsm1wn34i2hSAZBiw', null, null, null, null, 'Nicholas Fett is an economist, developer, and founder at Tellor, a decentralized oracle on the Ethereum network. Former positions include an economist at the US CFTC, the federal regulator for derivatives and cryptocurrencies, where he studied market structure of derivatives markets and implications of new technologies.', 'https://www.tellor.io?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (313, 'Atul Ajoy', 'Partner', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/Ajoy_Atul_Photo-Atul-Ajoy.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/atulajoy', null, null, 'https://www.linkedin.com/in/atul-ajoy/', null, null, 'Atul is a Partner at Horseshoe Capital, an investment firm that invests in early-stage (anywhere from just an idea to Series A) startups building on crypto, blockchain, and web3. Before Horseshoe Capital, Atul founded Chromata, a blockchain startup building a platform that allows businesses to issue verifiable credentials that allow their users and partners to prove who they are and what they can do.', 'https://www.horseshoe.vc/?utm_source=cryptoslate');
