INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (322, 'Cyrus Fazel', 'Founder & CEO', 'SwissBorg', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/cyrus-fazel-swissborg.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/cyrus_fazel', null, null, 'https://www.linkedin.com/in/cyrusfazelmrfintech', null, 'https://www.instagram.com/cyrus_fazel/', 'Born in Washington D.C. to Swiss German and Iranian parents, with 10+ years of experience in Asset Management & Algo Trading. A thought leader and influencer in the crypto and blockchain space, Cyrus is a frequent speaker spreading the idea that a decentralised, meritocratic system can reshape the future & create a more vibrant & equitable world of finance.', 'https://twitter.com/cyrus_fazel');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (323, 'Anthony Lesoismier Genieux', 'Co-Founder & CSO', 'SwissBorg', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/anthony-Lesoismier-Geniaux-swissborgjpg.jpg&w=60&h=60&q=75', null, null, null, null, null, null, 'https://www.linkedin.com/in/anthony-lesoismier-geniaux-06254a11/?locale=en_US', null, null, 'Anthony Lesoismier Genieux is of French-Armenian background and has a Master of Science from the French Engineering University, Polytech Nice-Sophia. With 10 years of experience in asset management in France & UK working with leaders of the Industry. Anthony is a frequent speaker & thought-leader in the crypto fintech space.', 'https://www.linkedin.com/in/anthony-lesoismier-geniaux-06254a11/?locale=en_US');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (324, 'Matt Huang', 'Co-founder', 'Paradigm', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/paradigm-matt-huang.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/matthuang', null, null, 'https://www.linkedin.com/in/kmhuang/', null, null, 'Matt Huang is co-founder and Managing Partner at Paradigm. Previously, Matt was a partner at Sequoia Capital focusing on early-stage venture investments including leading the firm’s cryptocurrency efforts. Matt was the founder and CEO of Hotspots, a YCombinator company acquired by Twitter in 2012, and angel investor in companies such as Bytedance and Instacart. He purchased his first Bitcoin from MtGox in 2012. Matt holds a B.S. in Mathematics from MIT.', 'https://www.matthuang.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (325, 'Ray Dalio', 'Co-Chief Investment Officer & Chairman', 'Bridgewater Associates', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/bridgewater-ray-dalio.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/RayDalio', 'https://www.youtube.com/channel/UCqvaXJ1K3HheTPNjH-KpwXQ', null, 'https://www.linkedin.com/in/raydalio/', null, null, 'Ray Dalio is the Founder and Co-Chief Investment Officer of Bridgewater Associates and a Director on its Operating Board of Directors. Ray has been a global macroeconomic investor for more than 50 years. He started Bridgewater out of his two-bedroom apartment in New York City in 1975 and under his leadership, the firm has grown into the fifth most important private company in the US according to Fortune Magazine.', 'https://twitter.com/RayDalio');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (326, 'Nayib Bukele', 'President', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2021/06/nayib-bukele-photo.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/nayibbukele', null, null, null, null, null, 'Nayib Armando Bukele Ortez is a Salvadoran politician and businessman who is the 46th and current President of El Salvador. He has served since 1 June 2019 after winning the 2019 election.', 'https://www.presidencia.gob.sv/?utm_source=cryptoslate');
