INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (332, 'Kathryn Haun', 'Founder', 'Haun Ventures', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/katie-haun.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/katie_haun', null, null, 'https://www.linkedin.com/in/kathryn-haun-0791456/', null, null, 'Katie is a General Partner at Andreessen Horowitz, a Silicon Valley based tech venture capital firm and co-chairs its crypto funds. Katie also serves as an independent director on the board of Coinbase, where she chairs its Nomination and Governance Committee and serves on its Compensation Committee, and on the board of HackerOne where she serves on its Audit and Risk Committee. She has testified before both houses of Congress on the intersection of technology and regulation, and teaches at Stanford’s GSB.', 'https://www.haun.co/?utm_source=cryptoslate');
