INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (338, 'Elnaz Sarraf', 'Founder & CEO', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/elnaz-sarraf-unigalax.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/elnaz_sarraf', null, null, 'https://www.linkedin.com/in/elnazsarraf/', 'https://t.me/unigalaxchannel', 'https://www.instagram.com/elnaz_sarraf/', 'Elnaz Sarraf is the CEO and founder of the award-winning ROYBI® Robot – the world’s first AI-powered smart toy to teach children language and STEM skills. It also has been named one of TIME Magazine’s Best Inventions in Education, on the 2019 CNBC Upstart 100 list as one of the world’s most promising startups, and on Fast Company’s 2019 World-Changing Ideas. Elnaz is also a Board Member at the Consumer Technology Association, Small Business Council, and member of Forbes Technology Council.', 'https://unigalax.io?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (339, 'Cathie Wood', 'Founder, CEO & CIO', 'Ark Invest', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/03/catherine-wood.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/cathiedwood', null, null, 'https://www.linkedin.com/in/catherinedwood/', null, null, 'Cathie registered ARK Investment Management LLC (“ARK”) as an investment adviser with the U.S. Securities and Exchange Commission in January 2014. With over 40 years of experience identifying and investing in innovation, Cathie founded ARK to focus solely on disruptive innovation while adding new dimensions to research. Through an open approach that cuts across sectors, market capitalizations, and geographies, Cathie believes that ARK can identify large-scale investment opportunities in the public markets resulting from technological innovations centered around DNA sequencing, robotics, artificial intelligence, energy storage, and blockchain technology. As Chief Investment Officer (“CIO”) and Portfolio Manager, Cathie led the development of ARK’s philosophy and investment approach and has ultimate responsibility for investment decisions.', 'https://ark-invest.com/?utm_source=cryptoslate');
