INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (354, 'Fred Wilson', 'Founder & Partner', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/04/fred-wilson.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/fredwilson', null, null, 'https://www.linkedin.com/in/fredwilson/', null, null, 'Fred Wilson is an American businessman, venture capitalist and blogger. Wilson is the co-founder of Union Square Ventures, a New York City-based venture capital firm with investments in Web 2.0 companies such as Twitter, Tumblr, Foursquare, Zynga, Kickstarter, Etsy and MongoDB.', 'https://avc.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (355, 'Esteban Ordano', 'Advisor', 'Decentraland', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/04/esteban-ordano.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/eordano', null, null, 'https://www.linkedin.com/in/eordano/', null, null, 'Esteban Ordano is a software engineer who co-founded Decentraland alongside Ari Meilich, who moved on to found Big Time Studios in April 2020. Ordano held the position as Decentraland’s chief technology officer, though he recently stepped back to a more advisory role after the platform became a decentralized autonomous organization (DAO). Meilich also holds an adviser position in the project despite his departure. Meilich and Ordano are Argentinian software developers with varying backgrounds in the crypto space.', 'https://twitter.com/eordano');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (356, 'Jack Mallers', 'CEO & Founder', 'Strike', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/04/jack-mallers.jpg&w=60&h=60&q=75', null, 'https://jimmymow.medium.com/', null, 'https://twitter.com/jackmallers', null, null, null, null, null, 'CEO of Zap, a bitcoin investment and payments company that transacts over the Lightning Network. Zap recently closed a $3.5M seed round led by Greenoaks Capital and employs about 20 people. It recently announced it’s working with Visa as the payments and credit card giant introduces clients to bitcoin. Mallers’ father founded and sold one of the largest futures brokerages in Chicago and introduced him to bitcoin in 2013.', 'https://zaphq.io/?utm_source=cryptoslate');
