INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (358, 'Brian Fu', 'Co-Founder & Co-Project Lead', 'zkLend', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/04/brian-fu-1.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/bfu_fubrian', null, null, 'https://www.linkedin.com/in/brian-fu-58206715/', null, null, 'Brian has more than 15 years of experience in TradFi, investment banking and private equity at major financial institutions including Bloomberg, Standard Chartered Bank, Silk Road Finance and China Merchants Capital, before diving into the world of on-chain protocols and crypto. Having experienced the bottlenecks and pain points of TradFi infrastructure firsthand, he looks forward to building with people and technology that can realise this new financial paradigm. Brian is a graduate of the University of British Columbia with a major in Engineering Physics and a minor in Commerce, during which he developed a keen interest in computational finance.', 'https://zklend.com?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (359, 'Jane Ma', 'Co-Founder & Co-Project Lead', 'zkLend', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/04/jane-ma-1.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/jane0mac', null, null, 'https://www.linkedin.com/in/jane-ma/', null, null, 'Jane is the Co-Founder and Co-Project Lead at zkLend. She started her career at Credit Suisse before making her way to Deliveroo. Working initially as Operations Manager and later on Chief of Staff at Deliveroo Hong Kong, Jane fell in love with the high octane startup environment and work culture. She has extensive experience helping scale business operations at various start-ups.', 'https://zklend.com/?utm_source=cryptoslate');
INSERT INTO public.crypto_people (id, name, position, company, avatar, blog, medium, reddit, twitter, youtube, facebook, linkedin, telegram, instagram, about, website) VALUES (360, 'Janet Yellen', 'Secretary of the Treasury', '', 'https://cryptoslate.com/wp-content/themes/cryptoslate-2020/imgresize/timthumb.php?src=https://cryptoslate.com/wp-content/uploads/2022/04/janet-yellen.jpg&w=60&h=60&q=75', null, null, null, 'https://twitter.com/SecYellen', null, null, null, null, null, 'Janet Louise Yellen is an American economist and educator serving as the 78th United States secretary of the treasury under President Joe Biden since January 26, 2021. A member of the Democratic Party, she previously served as the 15th chair of the Federal Reserve from 2014 to 2018.', 'https://twitter.com/SecYellen');
