INSERT INTO public.product_feature (id, product_name, feature) VALUES (195, 'Everdragons', 'ERC-721 TOKEN. Every Everdragon is an ERC-721 Token -  embedded on the Ethereum Blockchain. The owner has the only key to this Everdragon. Everdragons can’t be deleted -  changed or taken away.');
